const int STEPS_INT = 500;
const float STEPS_FLOAT = float(STEPS_INT);
const vec4 BLACK = vec4(vec3(0.0), 1.0);

float hue2rgb(float f1, float f2, float hue) {
    if (hue < 0.0)
        hue += 1.0;
    else if (hue > 1.0)
        hue -= 1.0;
    float res;
    if ((6.0 * hue) < 1.0)
        res = f1 + (f2 - f1) * 6.0 * hue;
    else if ((2.0 * hue) < 1.0)
        res = f2;
    else if ((3.0 * hue) < 2.0)
        res = f1 + (f2 - f1) * ((2.0 / 3.0) - hue) * 6.0;
    else
        res = f1;
    return res;
}

vec3 hsl2rgb(vec3 hsl) {
    vec3 rgb;
    while(hsl.x > 1.0) {
    	hsl.x -= 1.0;
    }
    if (hsl.y == 0.0) {
        rgb = vec3(hsl.z); // Luminance
    } else {
        float f2;

        if (hsl.z < 0.5)
            f2 = hsl.z * (1.0 + hsl.y);
        else
            f2 = hsl.z + hsl.y - hsl.y * hsl.z;

        float f1 = 2.0 * hsl.z - f2;

        rgb.r = hue2rgb(f1, f2, hsl.x + (1.0/3.0));
        rgb.g = hue2rgb(f1, f2, hsl.x);
        rgb.b = hue2rgb(f1, f2, hsl.x - (1.0/3.0));
    }   
    return rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 realPos = vec2(0.001643721971153, 0.822467633298876);
    vec2 pos = ((fragCoord / iResolution.yy - vec2(1, 0.5)) / pow(1.5, iTime) + realPos);
    vec2 z = vec2(0);
    bool escaped = false;
    for (int i = 0; i < STEPS_INT; i++) 
    {
		z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + pos;
        if (length(z) > 2.0) {
			// Escaped
            escaped = true;
            fragColor = vec4(hsl2rgb(vec3(float(i) / STEPS_FLOAT * 10.0, 1.0, 0.8)), 1.0);
            break;
		}
	}
    if (!escaped)
        fragColor = BLACK;
}
