// Fabrice 266 chars version !

void mainImage( out vec4 a, vec2 b )
{
    b = b*154./iResolution.y - 64.;
    a -= a - 1.; 

    ivec2 d = ivec2(94595,843211);

    for ( int i=0; i<5; i++, b.x -= 42. ) 
       length(b) < 26.
          ?  a *= 1. -  texelFetch(iChannel0, ivec2(b) + d%16*64 + ivec2(32,-32) ,0 )
          :  a ,
       d /= 16 ;


    a = (1. - a) * (a.a - .44)/.05;
    a -= sin( iTime*2.-a.rbga*3. ) *.5 + .5;

    //a.rg *= 1e2/(5. + length(b));
}
