// false-3D version of danb https://www.shadertoy.com/view/ldjfzd
// see golfed version here: https://www.shadertoy.com/view/XsjfWG
// original animgif: https://giphy.com/gifs/boxes-26n6QQ5MVq4eHk8HC

float pi = 3.1415927;

float wave_model(float phi)
{
    phi = mod(phi, 4. * pi); // was 3.5
    return  min( min(phi,pi/2.) , max(5.*pi/2. - phi, 0.) );
}

#define W(dt) ( smoothstep( 0., 1., wave_model(t + dt)) / 4. + .2 )
//#define W(dt) ( smoothstep(wave_model(t + dt), 0., 1.) * .8 + .2 )

void mainImage(out vec4 O, vec2 U)
{
    U =  7.*( U+U - iResolution.xy)  / iResolution.y;
	float t = iTime*2., S = 1.732; // sqrt(3) 
    O = vec4(.15);

    for (float k=-1.; k<2.; k++)
      for (float j=-1.; j<2.; j++)
        for (float i=1.; i>-2.; i--) {
            float a = i* W(pi/2.),                                    // place cube
                  b = j* W(pi),
                  c = k* W(0.);          
            vec2 V = U - vec2( a+c, (a+b*2.-c)/S ) * 5.;

	        vec4 P = vec4(V, V.x+V.y*S, V.x-V.y*S);                   // render cube
            if (abs(P.x) < 1. && abs(P.z) < 2.  && abs(P.w) < 2.  ) 
                O =   P.x>0. && P.w>0. ?  vec4(.9, .6, .05, 1)        // 3 faces
                    : P.x<0. && P.z<0. ?  vec4(.8, .2, .2,  1) 
                    :                     vec4(.1, .55,.6,  1);
        }
}
