/**  // 248 chars -  B&W version
#define r(i)   smoothstep(.06,.0, abs(length(U-vec2(i,0))-.85) -.08) * (.6+.4*(U.x-i))

void mainImage( out vec4 O,  vec2 U )
{
	U /= iResolution.y;
    U = 2.*fract(U*5.) -1.;

    O +=  max ( r(0.), max( r(-1.) , r (1.))) -O;

    U.x = -U.x;
    U.y -= sign(U.y); // mod(U.y,2.)-1.;
    O =  max ( max(O, r(0.) ), max( r(-1.) , r (1.)));

}
