// 1 1,13  1,D
// 7 7,13  7,D
// ! 1,14  1,E

//        0x1719F4, => 1513972
//        0xEDD9A9

void mainImage( out vec4 a, vec2 b )
{
    a -= a;
    b /= 3e3;//b /= 1e3; b /= iResolution.xy;

    // could crash GPU ?
    float s = max(sin(iTime)*.4+.9,1.)/16.; // optimized, 16. not changeable anymore
    // float s = 1./16.; // no GPU crash

    vec2 c,d = vec2(
        2441603,
        0x9AAAAB
    );

    for( c = vec2(s);
        c.x < s*7.;
        c.x += s*.5
    )
       a += 
           texture(iChannel0,
             b-c + ( mod(d,16.) + vec2(.5,-.5) )*s
           ).r
           * step(length(b-c),s/2.)
       ,
       d = c.x == s*3.5 ? vec2(
            1513972,
            0xEDD9A9
          ) : floor(d*s)
       ;
}
