#define rectifier(v) clamp(v, 0., 1.)
vec3 imageNeuralNet(vec2 uv) {float v4 = rectifier(.2746111+uv.y*1.389169+uv.x*-1.388435);float v3 = rectifier(-.6879576+uv.y*-.9478971+uv.x*.8932457);float v2 = rectifier(-.9900636+uv.y*-.9024127+uv.x*.817225);float v1 = rectifier(-.6767375+uv.y*-.9119775+uv.x*.890689);float v0 = rectifier(-.0903738+uv.y*.946999+uv.x*.78622);return vec3(rectifier(-.0115425+v4*-.8898268+v3*1.386728+v2*.9918661+v1*1.150873+v0*.6685397),rectifier(.0341423+v4*-.4374903+v3*1.169702+v2*1.013205+v1*1.013135+v0*.4470741),rectifier(.0673083+v4*-.1249536+v3*.6991955+v2*.90826+v1*.7934351+v0*.2805905));}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y = 1.0-uv.y;

	fragColor = vec4(imageNeuralNet(uv).zyx, 1.);
}
