// Ink droplets in Poiseuille Flow by maq/flp
// (Poiseuille Flow)
// See: http://www.thermopedia.com/content/1042/
// http://panoramx.ift.uni.wroc.pl/~maq/eng/index.php

float dist(vec2 r, vec2 c) {	return length(r-c);	}
float saw(float t, float a) {	return 0.5+1.0*(t/a-floor(0.5+t/a));  }
float poiseuille(float r, float R) { return (0.25 - (r/R)*(r/R)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;	    
	float d, tt, tt2;
	float RadiusI = 0.0125;
	float Radius = RadiusI;
	float c = 1.0;
	float s = 0.0;//0.05*sin(uv.x*2.5);		// @todo

   //c *= smoothstep(0.25,0., abs(uv.y+s -.5) -.25);
	c=smoothstep(0.25,0.256,uv.y+s);
	c*=(1.0-smoothstep(0.75,0.756,uv.y+s));

	float xx;
	for(float kulka=0.0; kulka<1.0; kulka+=0.015)//05)
	{
		tt = texture( iChannel0, vec2(kulka,0.02) ).r;	
		tt2 = texture( iChannel0, vec2(kulka,0.06) ).r;	
		//Radius= RadiusI;
		Radius = 0.03+Radius * 3.0*abs(uv.y-0.5);
		//xx = ar * saw(tt+0.25*iTime* pow(poiseuille(uv.y-0.5,0.5),1.0) ,1.0);
        xx = saw(tt+2.75*iTime*poiseuille((uv.y-0.5),0.5),1.0);

		d=dist(uv, vec2(xx, 0.25+0.5*tt2));
		//if(d < Radius) // FN2
		c*=smoothstep(Radius*0.3,Radius,d);       
        //c *= smoothstep(Radius,Radius*1.3, abs(uv.y+s -.5) -.25);
	}		

	fragColor = vec4(c);		//vec3(c,c,c),1.0);  // FN2
}
