void zebra(inout vec4 O, in vec2 uv, in float t, in float speed, float bands) {
    float d = uv.y + t * speed;
    O = vec4(smoothstep(.4, .41, abs(1.0 + sin( d * bands))*0.5));
}
void dots(inout vec4 O, in vec2 uv, in float t, in float speed) {
    uv.y -= iTime / 10.0;
    O = vec4(round(1.0-length(vec2(0.5) - fract(uv*20.))*5.));
}
void mainImage( out vec4 O, in vec2 U )
{
    vec2 uv = U.xy / iResolution.y, mouse = (vec2(iMouse.xy / iResolution.y) - .5 )*2.;
    float t = iTime, a = atan(mouse.y,mouse.x);
    uv-=.5;
    uv = mat2(cos(a),-sin(a),sin(a),cos(a)) * uv + .5;
    float speed = 0.1 + round( fract( cos( 3.37 * uv.x + 7.5 )) * 2. * (1. - iMouse.x / iResolution.x * 2.) -1.0);
    if(abs(speed) > .9) {
      zebra(O, uv, t, speed, 13.7);
    } else if(abs(speed) > .3) {
      dots(O, uv, t, speed);
    } else {
      zebra(O, uv, t, speed, 100.2);
    }   
}

