{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define HOOD_WIDTH 1
#define HOOD_HEIGHT 1

#define R0N 0.0
#define R1N 0.0
#define R2N 0.0
#define R3N 1.0
#define R4N 1.0
#define R5N 0.0
#define R6N 0.0
#define R7N 0.0
#define R8N 0.0

#define DENSITY 0.5
float rand(vec2 co){
  	return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * length(iDate));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cellWidth = 1.0/iResolution.xy;
   	vec2 uv = fragCoord.xy / iResolution.xy;

    //	total your neighbors
    float hoodTotal = 0.0;
    for( int yi = -HOOD_HEIGHT; yi <= HOOD_HEIGHT; yi++ )
    {
    	for( int xi = -HOOD_WIDTH; xi <= HOOD_WIDTH; xi++ )
        {
			vec2 xy = vec2(xi,yi) * cellWidth + uv;
            xy = vec2(mod(xy.x, 1.0), mod(xy.y, 1.0) );
            hoodTotal += texture(iChannel0, xy).r;
        }
    }

    //	dont count yourself
    hoodTotal -= texture(iChannel0, uv).r;

    //	sample the rule
    float c = 0.0;
    if( hoodTotal == 0.0 )
    {
        c = R0N;
    }
    else if( hoodTotal == 1.0 )
    {
        c = R1N;
    }
    else if( hoodTotal == 2.0 )
    {
        c = R2N;
    }
    else if( hoodTotal == 3.0 )
    {
        c = R3N;
    }
    else if( hoodTotal == 4.0 )
    {
        c = R4N;
    }
    else if( hoodTotal == 5.0 )
    {
        c = R5N;
    }
    else if( hoodTotal == 6.0 )
    {
        c = R6N;
    }
    else if( hoodTotal == 7.0 )
    {
        c = R7N;
    }
    else if( hoodTotal == 8.0 )
    {
        c = R8N;
    }

    fragColor = vec4(vec3(c),1.0);
    if (iMouse.z >=0.0)
    {
        float mc = step( distance(uv,(iMouse.xy/iResolution.xy)), 0.01 ) ;
        //fragColor = vec4(step(rand(uv),DENSITY));
    	fragColor = vec4(vec3(mc),1.0) * vec4(step(rand(uv),DENSITY));
    }
}
