// golfed version of "Shifting arrows animation" by qwertsol. 
// https://shadertoy.com/view/XdtBD7 (839) https://www.shadertoy.com/view/ls3BW7 (2162)

void mainImage(out vec4 O, vec2 u) {
    float  h = .5,
           t = iTime / 1.5,
           x = fract(t) - h,
           a = t + x - x* abs(x+x),
           p = 5. / iResolution.y;
    vec2   U = u*p, V, C = vec2(h, .25);
    int    n = int(t) % 4, i=-1;
    for ( n%2==1 ? U.y += h, a += 2. : a ;
          i++ < 6 ;
          O += smoothstep(-p,p, min( t<h||x<.25 ? t : t-h, t<h ? .25-x : 1.-x-t))
        )
        V = C + ( fract( n>1 ? U+h : U ) - vec2(i%3-1,i/3) -C )
               * mat2(cos( a*1.5708 + vec4(0,33,11,0))),
        x = abs(V.x-h), t = V.y;
    n%2==1 ? O= 1.-O : O; }





