#define RADIUS 0.12

float dist_to_line(vec2 p1, vec2 p2, vec2 uv)
{
	float a = abs(distance(p1, uv));
	float b = abs(distance(p2, uv));
	float c = abs(distance(p1, p2));

	float d = sqrt(c*c + RADIUS*RADIUS);

	if (a >= d || b >= d)	{
        float dist1 = distance(p1, uv);
        float dist2 = distance(p2, uv);
        return min(dist1,dist2);
	}

	float p = (a + b + c) * 0.5;

	float h = 2.0 / c * sqrt( p * ( p - a) * ( p - b) * ( p - c));
    return h;
}

float falloff(float dist)
{
    if (dist > RADIUS) {
        return 0.0;
    } else {
        return (RADIUS-dist)*10.0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.y / iResolution.x;
    uv.x /= aspect;
 	vec2 p1 = vec2(0.1/aspect, 0.5);
 	vec2 p2 = vec2(0.5/aspect, 0.5);
 	vec2 p3 = vec2(0.9/aspect, 0.5);

    float dist1 = dist_to_line(p1, p2, uv);
    float dist2 = dist_to_line(p2, p3, uv);
    float i1 = falloff(dist1);
    float i2 = falloff(dist2);

    float s = sin(iTime) * 0.5 + 0.5;    
 	fragColor = vec4(i1+i2*(1.0-s), s*i2, 0, 1);
}
