#define HASHSCALE1 0.231
#define HASHSCALE3 vec3(.1031, .1030, .0973)
#define epsilon vec2(0.01, 0)

//Dave_Hoskins hash
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return 2.0*fract((p3.xx+p3.yz)*p3.zy) - 1.0;

}

float noise(vec2 p){
    vec2 h = vec2(0, 1);

    vec2 i = floor(p);
    vec2 f = fract(p);

    vec2 d1 = f;
    vec2 d2 = vec2(f.x - 1.0, f.y);
    vec2 d3 = f - 1.0;
    vec2 d4 = vec2(f.x, f.y - 1.0);

    f = f*f*(3.0 - 2.0*f);

    vec2 v1 = hash22(i);
    vec2 v2 = hash22(i + h.yx);
    vec2 v3 = hash22(i + h.yy);
    vec2 v4 = hash22(i + h.xy);

    return mix(mix(dot(v1, d1), dot(v2, d2), f.x),
               mix(dot(v4, d4), dot(v3, d3), f.x), f.y);
}

float potential(vec2 p){
    float result = 0.05*noise(0.5*p);
    result += 0.0025*noise(5.0*p);
    result += 0.000125*noise(50.0*p);
    result += 0.01*iTime*p.x;
    return result;
}

vec2 curl(vec2 p){
    vec2 result = vec2(potential(p + epsilon.yx), potential(p + epsilon));
    result -= potential(p);
    result /= epsilon.x;
    return vec2(-result.x, result.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(0);
    fragColor.xy = uv;
    if(iFrame > 30){
        fragColor = texture(iChannel0, uv);
        fragColor.xy += 0.01*curl(uv);
    }
}
