#define PI 3.14159265
mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float random(vec2 p){return fract(sin(dot(p,vec2(12.,78.)))*4375.);}
float sdBox(vec3 p, vec3 b){
    vec3 d=abs(p)-b;
    return min(max(max(d.x,d.y),d.z),0.)+length(max(d,0.));
}

float ribbon(vec3 p){
    p.x+=sin(iTime+p.z)*.1;
    p.y+=.4+sin(iTime*2.+p.z*3.)*0.1-p.z*.1;
    p.xy*=rz2(sin(iTime+p.z)*.3);
    return sdBox(p,vec3(.2,.001,2.));
}

float picket(vec3 p){
    p.z-=2.;
    p.y+=1.;
    p.yz*=rz2(-1.);
    return max(length(p.xy)-.1,abs(p.z)-1.);
}

float terrain(vec3 p){
    p.x+=14.;
    vec2 q=p.xz*vec2(.05,.03);
    vec2 f=floor(q);
    float r=sin(q.x*PI)*sin(q.y*PI)*random(f)*3.;
    vec2 s=p.xz*vec2(.07,.03)*10.;
    s.y+=iTime*3.;
    s.x+=sin(s.y);
    float fft=0.;
    for(int i=0;i<20;++i)
    	fft+=pow(texture(iChannel0,vec2(float(i)*.005,.25)).r,10.);
    float r2=sin(s.x)*sin(s.y)*fft*.3;
    return p.y+1.5-r-r2;
}

float map(vec3 p){
    float d=ribbon(p);
    d=min(d,picket(p));
    d=min(d,terrain(p));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
	float a=iTime*.5;
    vec3 ro=vec3(uv+vec2(5.,3.)+vec2(cos(a),sin(a))*.5,-10.),rd=vec3(uv,1.),mp=ro;
    for (int i=0;i<50;++i){
        float md=map(mp);
        if(md<.001)break;
        mp+=md*rd;
    }
	fragColor = vec4(length(mp-ro)*.01);
}
