// http://thndl.com/square-shaped-shaders.html

#define TAU 6.2831853

mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

float st(float t,float n){return min(floor(mod(t,2.)*n)/n,ceil(mod(2.-t,2.)*n)/n);}
float rev(vec2 p){
    float a=atan(p.y,p.x);
    float r=TAU/(st(iTime,4.)*4.+3.);
    return cos(floor(.5+a/r)*r-a)*length(p);
}

float map(vec3 p)
{
    p.xy*=rz2(iTime*.3);
    p.xz*=rz2(iTime*.7);

    vec3 c=vec3(2.);
    p=mod(p+.5*c,c)-.5*c;

    p.yz*=rz2(iTime*.5);
    p.xz*=rz2(iTime*.7);

    float rex=rev(p.xy)-.5;
    float rin=rev(p.xy)-.3;

    return max(max(-rin,rex),abs(p.z)-.1)*.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy -.5*iResolution.xy)/iResolution.y;
    vec3 ro=vec3(uv,-1.),rd=vec3(uv,1.),mp=ro;
    for(int i=0;i<100;++i){
        float md=map(mp);
        if(md<.001)break;
        mp+=rd*md;
    }
	fragColor = vec4(length(mp-ro)*.1);
}
