#define PI 3.14159265359
void mainImage(out vec4 fragColor ,vec2 fragCoord )  

{  
    vec2 v = (fragCoord  - .5 * iResolution.xy) /  min(iResolution.x, iResolution.y);
    float t = fract((iDate.w * .07 + v.y)*17.);
    vec3 a = vec3( 1, .2, 0), b = -vec3(.1, 0, .01);
    fragColor.z = (v.y + 16.)*.0017;
    fragColor += v.y;
    fragColor.xyz += mix(length(v*0.7),pow(length(v*1.1) * .237,1.37),sin(iDate.w*.1)+1.);
    float q = step(t,1.3*v.y+.5) - length(v) * 3.;
    fragColor.xyz += mix(a,b,smoothstep(q,q+0.03,0.1));
    vec2 st = 66.0 * v + vec2(0,27);
    float aa = atan(st.x,st.y) + PI;
    float r = PI*2./3.0;
    float d = cos(floor(.5+aa/r)*r-aa) * length(st);
    fragColor.gb += vec2(1.0-smoothstep(.5,1.0,d*1.5))*1.5;
}

