vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}
#define SC(a)  vec2(sin(a),cos(a))
void mainImage( out vec4 O, in vec2 f ){
    vec2 U = (f-iMouse.xy) / iResolution.y;
    U*=3.0;
    vec3 c = vec3(0);
    for(float i=2., t = iTime*.7;i<38.;i++){
        U += i/20. * SC(i*11.1+t*.1);
		c[int(i)%3] += sin(atan(U.x,U.y) + i * 111.32 + t) * .33;
    }
    c += vec3(.7,.4,.4);
    vec3 q = rgb2hsb(c);

    vec2 uv = fract((f-iMouse.xy) / iResolution.y * 10.0) ;
    uv+= step( fract(uv.x),.5)*.5;
    float r = 37.0/iResolution.x;
    float e = smoothstep(r,r*0.7,length(uv-0.75));

    float grid = step( length( fract( f.xy / 150.) - .5), .01);
    O = vec4(q + e - grid, 1.);
}
