const vec3
white = vec3(1),
cream = vec3(.99, .97, .95),
sand = vec3(.9, .85, .74),
yello = vec3(1., 1., .37),
pink = vec3(1., .5, .5),
tomato = vec3(1., .3, .3),
red = vec3(.9, .05, .25),
acid = vec3(.0, .9, .6),
teal = vec3(.0, .55, .5),
wald = vec3(.1, .37, .25),
blue = vec3(.02, .01, .17),
dark = vec3(.05, .05, .05),
black = vec3(0);

const vec3[] colors = vec3[](
    white,
    cream,
    sand,
    yello,
    pink,
    tomato,
    red,
    acid,
    teal,
    wald,
    blue,
    dark,
    black
);

vec3 cc(float x){
   float n = float(colors.length());
   return colors[int(mod(x*n,n))];
}

#define click iMouse.z > .0
#define gold 1.61803

vec3 k(float yo){
    int index = int( mod(yo,float(colors.length())) );
    return colors[index];
}

vec3 list(vec2 uv, vec2 f){
        float t = iTime * 0.2;

    vec3 c = cc(uv.x);
    if(uv.y < .04){
        c = cc(iTime*0.01-uv.x);
    }

    if(uv.y > .3){
       c = vec3(mod(t/float(colors.length()),1.0));
    }else if(uv.y < .037 ){
       c = vec3(0);
    }

    if(uv.y > .5){
        vec3 a = mix(k(t-1.),k(t),uv.x),
  	    b = mix(k(t),k(t+1.),uv.x);
        c = mix(a,b,mod(t,1.));
    }

    if(uv.x-mod(t/float(colors.length()),1.0)<0.001){
        if(abs(uv.y-0.5)<0.01){
            c = vec3(mod(t/float(colors.length()),1.0));

        }
    }

     if(iResolution.y - f.y < 2. ){
       c = vec3(1);
    }

    return c;
}


vec3 detail(vec2 uv){  
    float fade = clamp(uv.y+sin(iTime),0.0,1.0);
    vec2 m =  iMouse.xy/iResolution.xy;
    vec3 solid = cc(uv.y > .5 ? m.x : m.y),
     gradient = mix ( cc(m.x), cc(m.y),fade),
     c = uv.x > 1./ gold ? solid : gradient;
    if(uv.x<.1){        
        c = mix ( cc(m.x), cc(m.y),floor(sin(uv.y*200.0)+1.0));     
    }
    return c;
}

void mainImage( out vec4 O, in vec2 f ){
    vec2 uv = f/ iResolution.xy;
    vec3 c = click ? detail(uv) :  list(uv,f);
    float p = iTime*0.2;
    if(abs(uv.y-0.5)>p){
      c = vec3(0);
    }
    O = vec4(c,1.);
}
