float scale = 2.0;
vec4 lumCoeff = vec4(0.2124,0.7153,0.0722,0.0);

float dist(vec2 p0, vec2 pf){return sqrt((pf.x-p0.x)*(pf.x-p0.x)+(pf.y-p0.y)*(pf.y-p0.y));}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; // normal tex coords
	vec4 color = texture(iChannel0,uv); // the normal color
  	vec4 grayscale = vec4(vec3(dot(color,lumCoeff)), 1.);

    float d = dist(iResolution.xy*0.5,fragCoord.xy) * (.005 + iMouse.x/iResolution.x * .02);
    // float d = sin(uv.x * 10.);
    vec4 mixed = mix(vec4(0.87, 0.501, 0.654, 1), vec4(0.955, 0.624, 0.614, 1), d);

    float brightness = 3.2;
    vec4 tinted = grayscale * mixed * brightness ;
    vec3 powz = vec3(1.2 + iMouse.x / iResolution.x * 3.);
    //fragColor = vec4(pow(mixed.xyz, powz), 1.);
    fragColor = vec4(pow(tinted.xyz, powz), 1.);
}

