#define M_PI 3.1415926535897932384626433832795

float weight(float dist)
{
    return pow(max(0., 1. - dist), 100.);
}

vec2 BezierDeg3(vec2 p0, vec2 p1, vec2 p2, float t)
{
    // bezier formula following bernstein Bez(t) = E(i->n)Bern(t).Pi with t=[0,1]
    // Deg3 -> Bez(t) = (1-t)Â²P0 + 2t(1-t)P1 +tÂ²P2
    return p0*pow(1.-t, 2.) + p1*2.*t*(1.-t) + p2*t*t;
}

vec3 Cardan(float p, float q)
{
    float p3 = p*p*p;
    float D = - (4.*p3+27.*q*q);
    if (D > 0.)
    {
        float a = 2.*sqrt(-p/3.);
        float b = acos(sqrt(27./(-p3))*-q/2.)/3.;
        return vec3(a*cos(b), a*cos(b+2.*M_PI/3.), a*cos(b+4.*M_PI/3.));
    }
    else if (D < 0.)
    {
        float coef = 1./3.;
        float DD = sqrt(-D/27.);
        float u = (-q+DD)/2.;
        u = sign(u)*abs(pow(abs(u), 1./3.));
        float v = (-q-DD)/2.;
        v = sign(v)*pow(abs(v), 1./3.);
        return vec3(u + v);
    }
    else
    {
        if (p == 0. || q == 0.)
            return vec3(0.);
        float r = 3.*q/p;
        float r2 = -3.*q / (2.*p);
        return vec3(r, r2, r2);
    }
}

vec3 resolveEqDeg3(float a, float b, float c, float d)
{
    vec3 A = vec3(b, c, d) / a;
    float p = A.y - A.x*A.x/3.;
    float q = A.x*(2.*A.x*A.x-9.*A.y)/27.+A.z;
    return Cardan(p, q) - (b / (3.*a));

}

float dBezier(vec2 pos, vec2 p0, vec2 p1, vec2 p2)
{
    // dP/dt(t) = 2(A+Bt)
    // simplified from 
    // dP/dt(t) = -2(1-t)P0 + 2(1-2t)P1 + 2tP2
    vec2 A = p1-p0;
    vec2 B = p2 - p1 - A;

    // search for equation
    // MP.dP/dt = 0
    vec2 M = p0 - pos;
    vec3 t = resolveEqDeg3(dot(B, B), dot(A, B)*3., dot(A,A)*2.+dot(M,B), dot(M,A));

    float d = 1000.;

    if (t.x >= 0. && t.x <= 1.)
        d = min(d, distance(pos, BezierDeg3(p0, p1, p2, t.x)));
	if (t.y >= 0. && t.y <= 1.)
        d = min(d, distance(pos, BezierDeg3(p0, p1, p2, t.y)));
    if (t.z >= 0. && t.z <= 1.)
        d = min(d, distance(pos, BezierDeg3(p0, p1, p2, t.z)));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy -0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 p0 = vec2(0.0,0.3);
    vec2 p1 = (iMouse.xy / iResolution.xy-0.5);//vec2(0.2,-.4);
    p1.x *= iResolution.x/iResolution.y;
    vec2 p2 = vec2(-.8,0.1);

    float w = 0.;
	w += weight(distance(p0,uv));
	w += weight(distance(p1,uv));
	w += weight(distance(p2,uv));
	w += weight(distance(BezierDeg3(p0,p1,p2, cos(iTime)*.5+.5), uv));

    w += weight(dBezier(uv, p0, p1, p2));

    fragColor = vec4(vec3(w), 1.);
}
