#define V vec3
#define Z normalize

V O, N, D, S, d, c,
    t,     
    Y = V(0,1,0);

float M, l, r, f, z=2.;

void C()
{
    for( int i=0; i<66; i++ )
    {
        S = clamp( ceil( O - .5 ), -z, z );
        r = O.y;
        S.y = .4 - mod( f = S.x * 5. + S.z, z ) * .15;
        l = length( O - S ) - S.y;
        M = -l;
        N = Y; 
        if ( l < r ) M = floor(f/z+6.), r = l, N = Z(O - S);

        O += D * r;
    }
}

void mainImage( out vec4 o, vec2 f )
{	
    t = iResolution;
	O = V(.85,2,-2.88);
    D = d = mat3( c = Z( cross(S = -Z(Y*.2+O),Y) ), cross(c,S), S ) * Z( V (f-t.xy/z , t.y ) );
    C();
    D = mod(O,z);
    t = O;    
	c = M < 0.
        ? V( D.x > 1. ^^ D.z < 1.? .4 : texture( iChannel0, .1*O.xz).x / z )
    			* smoothstep( 0., .5, length((O-S).xz) + step(1.5,max(t.x, t.z)) )
        : V( -8, 3, 6) * .01 * M + Y.yxx;
    D = Z( V(-6, 7, -5) );
    O += D * .01;
    t=N;
    C(); 
    M = M < 0. ? dot( t, D ) : 0.;    
    c *= M*V(1.2,1.02,.66) + (Y + t).y*V(5,7,10)/40.;
    o.rgb = sqrt(c + pow(max(0., dot( reflect( d, t), D ) ),16.)*M);
}
