#define PI 3.14159265359

float N21(vec2 p)
{	// Dave Hoskins - https://www.shadertoy.com/view/4djSRW
	vec3 p3  = fract(vec3(p.xyx) * vec3(443.897, 441.423, 437.195));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle), sin(_angle),cos(_angle));
}

float tri(vec2 uv){
    vec2 id = floor(uv)*0.02;
    float t = iTime*0.00001;
    uv = fract(uv);
    uv -= vec2(0.5);    
    float angle = N21(vec2(id.x,id.y)+t*0.001);
    angle = floor(angle*4.0)*0.5;
    uv = rotate2d( angle* PI ) * uv;
    uv += vec2(0.5);
    return smoothstep(0.99,1.0,uv.x + uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord-0.5)*0.5/iResolution.y;
   vec2 u = uv;
   vec3 f = vec3(1);

   uv *= 20.;
   f.g =  tri(uv)+u.x;

   uv*=.25;
   f.r= tri(uv)+u.y;

   uv*=.5*.5;
   f.b = tri(uv)+u.y+uv.y;

   fragColor = vec4(f,1.0);
}
