vec2 hash22(vec2 p)
{//https://www.shadertoy.com/view/4djSRW
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}
vec2 vf (vec2 v) {
	return texture(iChannel0, v).xy;
}
float ln (vec2 p, vec2 a, vec2 b) {
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
float ff (vec2 U, vec2 o) {
    float q = 0.2*iResolution.x;
	vec2 V = floor(U*q+0.5 + o)/q;
    V += 0.5*hash22(floor(V*q))/q;
    return max(1.-400.*ln(U, V, V+2.*vf(V)),0.);
}
void mainImage( out vec4 C, in vec2 U )
{
    U = U/iResolution.xy;

    float c = 0.;
    for (int x = -3; x <= 3; x++) {
    for (int y = -3; y <= 3; y++) {
        c += 0.4*ff(U,vec2(x,y));
    }
    }
    vec4 g = texture(iChannel0,U);
    C = vec4(vec3(c)+vec3(0.1*g.z,-0.1*g.z,10.*abs(g.w)),1);
}

