vec4 t (vec2 v) {return texture(iChannel0,fract(v/ur));}
float area (vec2 a, vec2 b, vec2 c) { // area formula of a triangle from edge lengths
    float A = length(b-c), B = length(c-a), C = length(a-b), s = 0.5*(A+B+C);
    return sqrt(s*(s-A)*(s-B)*(s-C));
}
mat2 rot (float a) {
	float s = sin(a), c = cos(a);
    return mat2 (c,-s,s,c);
}
vec2 perp (vec2 v) {return vec2(-v.y,v.x);}
void mainImage( out vec4 Co, in vec2 uu )
{
    U = uu;
    ur = iResolution.xy;
    if (iFrame < 1 || U.x < 10.||ur.x-U.x<10.) {

        Co = vec4(0.05,0,0,0);
    } else {
        vec2 v = U,
             A = v + vec2( 1, 1),
             B = v + vec2( 1,-1),
             C = v + vec2(-1, 1),
             D = v + vec2(-1,-1);
        float to = 0.;
        for (int i = 0; i < 5; i++) {
            vec2 tmp = t(v).xy;
            to += length(t(v+perp(tmp)).xy)-length(t(v-perp(tmp)).xy);
            v -= tmp;
            A -= t(A).xy;
            B -= t(B).xy;
            C -= t(C).xy;
            D -= t(D).xy;
        }
        vec4 me = t(v,0,0);
        vec4 n = t(v,0,1),
            e = t(v,1,0),
            s = t(v,0,-1),
            w = t(v,-1,0);
        vec4 ne = .25*(n+e+s+w);
        me = mix(me,ne,vec4(0.1,0.1,1,0.05));
        me.w -= 0.1*to;
        me.z  = me.z - (area(A,B,C)+area(B,C,D)-4.);
        vec4 pr = vec4(e.z,w.z,n.z,s.z);
        me.xy = me.xy + vec2(pr.x-pr.y, pr.z-pr.w)/ur;
        me.xy = rot(me.w*0.15915494309)*me.xy;


        if (length(U-vec2(0.1,0.5)*ur)<15.||length(iMouse.xy-U)<10.) me.xyw *= 0.;
        Co = me;
        Co.xyz = clamp(Co.xyz, -40., 40.);
    }
}

