// simplified & golfed variant of https://www.shadertoy.com/view/lljczz

/**/ // coyote : -10 chars

#define W(p) vec2( -(p).y, p ) / dot(p,p)

void mainImage(out vec4 O, vec2 U) {
    vec2 R = iResolution.xy,
         u = (U+U-R)/R.y,
         m = vec2( sin(iTime), .2*cos(iTime/3.7) ),
         P = ceil(u*20.-.5) / 20.,
         L = R-R + length( m = W(P-m)-W(P+m) );
    O += length( (u-P) * mat2( m*60./L/L, W(m)*L*R.y/2.) ); }




/** // more readable version about the maths of physics :


#define W(p) vec2( -(p).y, p ) / dot(p,p)

#define mainImage( O , U )                         \
    vec2 R = iResolution.xy,                       \
         u = (U+U-R)/R.y,                          \
