         P = ceil(u*20.-.5) / 20.,                 \
         V =  W(P-m) - W(P+m);                     \
    float l = length(V);                           \
    O += length( (u-P) * mat2( V*60./l, vec2(-V.y,V)*R.y/2.) ) / l

