// The following block is needed for Clubber integration
#ifndef CLUBBER
vec4 iMusic[4];
const float iTransition = 1.0;
#define CLUBBER_R abs(sin(iTime * 1.33))
#define CLUBBER_G abs(cos(iTime * 0.66))
#define CLUBBER_B abs(sin(iTime) * cos(iTime))
#define CLUBBER_A min(abs(cos(iTime * 0.33)),abs(sin(iTime * 0.66)))
#endif
// Clubber end

void mainImage(out vec4 color, vec2 fragCoord) {
     vec2 uv = fragCoord/iResolution.xy;
     float aspect = iResolution.x/iResolution.y;
     uv.x *= aspect;
     vec3 rv = vec3(0.);
     vec2 center = vec2(0.5 * aspect,0.5);
     rv.x = max(0.4, CLUBBER_R);
     rv.y = mix(0.05, rv.x * 0.6, CLUBBER_G);
     rv.z = mix(rv.y * 1.2, rv.x * 0.9, CLUBBER_B);
     rv *= 0.49;
     float d = distance(center, uv);
     float f = fwidth(d) * 3.;
     float c1 = smoothstep(rv.x - f, rv.x + f, d);
     float c2 = smoothstep(rv.y - f, rv.y + f, d);
     float c3 = smoothstep(rv.z - f, rv.z + f, d);
    color.rgb = abs(CLUBBER_A - vec3(c3 < 1. ? ( c2 < 1. ? c2 : 1.0 - c3):c1));
}
