// we must manually set this up in all the buffers
#define RockPaperScissors iChannel0
#define Webcam iChannel1
#define WebcamMovingAverage iChannel2
#define WebcamDiff iChannel3

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

const vec3 colors[2] = vec3[](
vec3(42., 157., 143.) / 255.,
vec3(255., 157., 143.) / 255.
);

vec3 colormap(float v) {
    int i = int(v * 2.);
    int i1 = i + 1;
    float lerp = v * 2. - float(i);
    vec3 colorStart = colors[i];
    vec3 colorEnd = colors[i1];
    return mix(colorStart, colorEnd, lerp);
}

/*


RockPaperScissors, should be iChannel0


 */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;   
    vec3 rgb = texture(RockPaperScissors, uv).xyz;
    vec3 webcam = texture(WebcamDiff, uv).rgb;


    // each step there's a growth component:
    // each component value x grows by x*(1-x)

    // vec3 growth = rgb * (1. - rgb);
    vec3 growth = rgb;
    // growth *= 1. + webcam * 4.;

    // each step there's a death component:
    // red beats green
    // green beats blue
    // blue beats red

    // red loses blue%
    // green loses x% of itself, where x is red (e.g. red = 0.5, green loses 50%)
    // blue loses green%

    vec3 death = vec3(
        - rgb.r * rgb.b,
        - rgb.g * rgb.r,
        - rgb.b * rgb.g
    );

    // diffusion component:

    vec3 rgbAvg = vec3(0., 0., 0.);
    for (float i = -1.; i <= 1.; i += 1.) {
        for (float j = -1.; j <= 1.; j += 1.) {
            float manhattanDist = abs(i) + abs(j);
            float weight = pow(2., 2. - manhattanDist) * (1. / 16.);
            rgbAvg += texture(iChannel0, (fragCoord + vec2(i, j)) / iResolution.xy).rgb * weight;
        }
    }
    vec3 diffuse = rgbAvg - rgb;

    // species-specific traits:
    // r increases by g
    // green gets a secondary growth: 0.01
    // blue gets sqrt(blue) growth
    vec3 traitsGrow = rgb * vec3(
        rgb.g,
        0.1,
        sqrt(rgb.b)
    );

    // vec3 dRgb = growth * 1.2 + death * 6.0 + diffuse * 1.3 + traitsGrow * 0.1;

    // vec3 dRgb = traits * 0.5;

    // float growthFactor = mix(0.4, 0.4, uv.x);
    float growthFactor = length(webcam) * length(webcam) * 5. + 0.03;
    float deathFactor = 6.; // mix(0., 25., uv.y);
    // float deathFactor = length(webcam) * length(webcam) * 5. + 0.5;
    // float diffuseFactor = mix(0., 2.5, uv.y) * sin(iTime);
    float diffuseFactor = length(webcam) * length(webcam) * 10.;
    // float diffuseFactor = 0.5;


    vec3 dRgb = growth * growthFactor + death * deathFactor + diffuse * diffuseFactor + traitsGrow * 0.5;

    float dt = 0.1;
    rgb = rgb + dRgb * dt;
    rgb = max(min(rgb, vec3(1.)), vec3(0.));

    if (iFrame <= 0) {
		rgb = vec3(
            rand(uv),
            rand(uv*2. + vec2(321., 459.)),
            rand(uv*3. + vec2(-4.13, 594.13))
        );
        /*rgb = vec3(0.1);
        if (length(fragCoord - iResolution.xy / 2.) < 50.) {
            rgb.r = 1.;
        }*/
    }
    fragColor = vec4(rgb, 1.0);
}

