void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 oldColor =
        texture(WebcamMovingAverage, uv);
    /*
        texture(iChannel2, uv) * 0.5 +
        (
        texture(iChannel2, uv + vec2(1., 0.) / iResolution.xy) +
        texture(iChannel2, uv + vec2(-1., 0.) / iResolution.xy) +
        texture(iChannel2, uv + vec2(0., 1.) / iResolution.xy) +
        texture(iChannel2, uv + vec2(0., -1.) / iResolution.xy)
            ) / 8.;
        ;
	*/
    vec4 newColor = texture(Webcam, uv);
    float lerp = 0.3;
    fragColor = vec4((oldColor * (1. - lerp) + newColor * lerp).xyz, 1.);
    // fragColor = newColor;
    // fragColor = (newColor - oldColor);
}

/*


WebcamDiff, should be iChannel3


*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = abs(texture(Webcam, uv).xyz - texture(WebcamMovingAverage, uv).xyz);
    col *= 10.; // amplify difference
    col = clamp(col, vec3(0.), vec3(1.));
    fragColor = vec4(col,1.0);
}
