#define VELAPPEARPOINTS 5.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv-0.5;
    p.x*=iResolution.x/iResolution.y;    
	p*=1.5;

    float angle = 3.14159/2.*mod(floor(iTime),4.)+3.14159/2.*smoothstep(0.,1.,mod(iTime,1.));
    p=mat2(cos(angle),sin(angle),-sin(angle),cos(angle))*p;
    p*=(0.5+
        mix(1.-1.*sin(15.1*mod(floor(iTime),4.)),
            1.-1.*sin(15.1*mod(floor(iTime)+1.,4.)),
            smoothstep(0.,1.,mod(iTime,1.))
           ));

    vec3 col = 0.2*vec3(0.3,0.5,0.7);
    col += 1.5*max(step(0.99,cos(50.*p.y)),step(0.99,cos(50.*p.x)))*col;
    col += (1.-smoothstep(-0.01,0.01,length(p-vec2(1.,0.))-0.02+0.005*sin(13.*iTime)))*vec3(0.,0.7,0.5);
    col += (1.-smoothstep(-0.01,0.01,length(p-vec2(-1.,0.))-0.02+0.005*sin(13.*iTime)))*vec3(0.,0.7,0.5);

    col-=0.1*fract(0.5*iTime+123.*sin(12.3*floor(50./(2.*3.14159)*p.x))+341.*sin(12.3*floor((50./(2.*3.14159))*p.y)));

    int np = 2+int(mod(VELAPPEARPOINTS*iTime,15.));
    float st = 1./float(np-1);
    st = st*(1.2-0.7*sin(iTime));

    for(int i=0;i<np;i++){
    	float x = st*float(i)-st*float(np-1)/2.; 
        col = mix(col+0.2*vec3(1.,0.5,0.),col,smoothstep(-0.1,0.1,length(p-vec2(x,0.))-0.01));
        col += (1.-smoothstep(-0.003,0.003,length(p-vec2(x,0.))-(0.005-0.003*sin(iTime))))*vec3(1.,0.5,0.);

    }

	col *= smoothstep(1.9,0.5,length(p)+0.1*sin(3.*atan(p.y,p.x)+10.*iTime));

    float gamma = 0.8;
	col.x = pow(col.x,gamma+0.15*sin(iTime+0.3));
    col.y = pow(col.y,1.3*gamma+0.15*sin(iTime));
    col.z = pow(col.z,1.2*gamma+0.15*sin(2.1*iTime-0.4));

    fragColor = vec4(col,1.0);
}
