#define transition 0.5
#define patternSquares 2.

//#define ZOOM
//#define REPEAT
//#define PATERN

#define rot(a) mat2(C=cos(a),S=-sin(a),-S,C)
#define rand(co) fract(sin(mod(dot(co ,vec2(12.9898,78.233)),3.14))*43758.5453)
#define p(uv,col) vec4(mod(floor((uv).x*patternSquares) + floor((uv).y*patternSquares), 2.))*col
#define t(uv) texture(iChannel0,uv)

#ifdef PATERN
#define f(uv,col) p(uv,col)
#else
#define f(uv,col) t(uv)
#endif


const highp float pi = 3.141592653589793;

void mainImage( out vec4 o, vec2 u ){
    #ifdef ZOOM
    vec2 uv = u/iResolution.x/(1.+sin(iTime/3.)/1.3)*7.;
    #else
    vec2 uv = u/iResolution.x*7.;
    #endif
    #ifdef REPEAT
    o=texture(iChannel0,uv);
    #else
    float C,S,
          dist = length(2.*fract(uv)-1.), 
          r = rand(floor(uv));

    o = mix(f((uv+r) * rot(pi*r*2.), vec4(1,0,1,0)), 
            f(uv * rot(pi-1.),vec4(0,1,1,0)), 
            smoothstep(1.-transition,.93, dist));
    #endif
}
