#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define S(v,r) smoothstep( 3.*40./R.y, 0., abs(v)-(r) )
vec2 R;
        //               perihelion,     ascending+arg_p,     radius, 
        //        aphelion,  |   eccentricity, |      period,    |         color
        //          (UA)    (UA)      (1)  (degrees)  (years) (k.km)     (iR,iG,iB)
vec4 C( vec2 U, float D, float d, float e, float a, float T, float r, int iR,int iG,int iB ) 
{
    float Rx = (D+d)/2.,               // major radius
           t = 6.28*iTime/T/2.;
           a = radians(a);
           r = iMouse.z <= 0. ? .1 : .03*r;                                 // click: sizes
    vec2   A = vec2( 1, sqrt(1.-e*e) ),  // A*Rx = major and minor radius
           C = vec2( (D-d)/2., 0 );      // ellipse center
    return .15*S( length( ( rot(-a) * U - C ) / A ) - Rx, 0.)               // orbit
             + S( length( rot(a)*( vec2(cos(t),sin(t))*Rx*A + C ) - U), r)  // planet
                * vec4(iR,iG,iB,0)/255.;
}

void mainImage( out vec4 O, vec2 U )
{
	R = iResolution.xy;
    U = (U+U-R)/R.y * 40.; U.y -= 7.;
    O -= O;
    //             perihelion,   ascending+arg_p,       radius, 
    //        aphelion, | eccentricity, |       period,   |      color
    //          (UA)   (UA)   (1)   (degrees)    (years)(k.km) (iR,iG,iB)
