    O += C( U,  5.46,  4.95, .049, 100.5+273.9,  11.86, 69.9, 203,162,134); // Jupiter
    O += C( U, 10.12,  9.04, .057, 113.7+339.4,  29.4,  58.2, 154,146,110); // Saturn
