vec3 qHit, qnHit, sunDir, msOrg;
float tCur, tCyc, dstFar, msRad, msDep, msDir, mDskRad, frmWid, growPhs, rAngH,
   rAngA, rAngSh;
int idObj;
bool isRefl, onRob;

float PrRndBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float s);
float PrCylDf (vec3 p, float r, float h);
float PrRCylDf (vec3 p, float r, float rt, float h);
float SmoothBump (float lo, float hi, float w, float x);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec2 Rot2D (vec2 q, float a);

const float pi = 3.14159;

float RobDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 s;
  float d, ds, szFac;
  p.xz = vec2 (- p.z, p.x);
  szFac = 6.;
  p *= szFac;
  dMin *= szFac;
  p.yz = Rot2D (p.yz, rAngSh);
  q = p;
  q.y -= 2.;
  ds = PrRCylDf (q.xzy, 1.6, 1.5, 2.);
  q = p;  q.y -= 2.3;
  d = max (PrSphDf (q, 0.85), - q.y);
  q.y -= -0.08;
  d = min (d, PrCylDf (q.xzy, 0.75, 0.1));
  q = p;  q.y -= 1.55;
  d = min (d, PrRCylDf (q.xzy, 0.9, 0.28, 0.7));
  q = p;  
  q.xz = Rot2D (q.xz, rAngH);
  q.x = abs (q.x) - 0.3;  q.y -= 3.1;
  q.xy = Rot2D (q.xy, 0.2 * pi);
  q.y -= 0.25;
  d = min (d, PrRCylDf (q.xzy, 0.06, 0.04, 0.3));
  q = p;  q.x = abs (q.x) - 1.05;  q.y -= 2.1;
  q.yz = Rot2D (q.yz, rAngA);
  q.y -= -0.5;
  d = min (d, PrRCylDf (q.xzy, 0.2, 0.15, 0.6));
  q = p;  q.x = abs (q.x) - 0.4;  q.y -= 0.475;
  d = min (d, PrRCylDf (q.xzy, 0.25, 0.15, 0.55));
  d = mix (ds, d, growPhs);
  if (d < dMin) { dMin = d;  idObj = 2; }
  q = p;
  q.xz = Rot2D (q.xz, rAngH);
  q.x = abs (q.x) - 0.4;  q.yz -= vec2 (2.7, 0.7);
  d = PrSphDf (q, 0.15);
  if (d < dMin) { dMin = d;  idObj = 3; }
  dMin /= szFac;
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  if (! isRefl) {
    d = dMin;
    q = p;
    q.y -= msOrg.y;
    d = min (d, PrCylDf (q, mDskRad + frmWid, msDep));
    q.z -= -0.01;
    d = max (d, - PrCylDf (q, mDskRad, msDep));
    if (d < dMin) { dMin = d;  idObj = 1; }
    q = p;
    q.y -= 0.2 * frmWid;
    d = PrRndBoxDf (q, vec3 (0.4, 0.2 * frmWid, msDep), 0.01);
    if (d < dMin) { dMin = d;  idObj = 1; }
  }
  if (onRob) {
    q = p;
    q.xz = Rot2D (q.xz, (0.5 + floor (atan (q.z, - q.x) * (15. / (2. * pi)))) * (2. * pi / 15.));
    q.x -= -2.;
    dMin = RobDf (q, dMin);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = 0; j < 40; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.025;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

#define SQRT3 1.7320508

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./SQRT3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (SQRT3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec3 HexGrid (vec2 p)
{
  vec2 q;
  p -= HexToPix (PixToHex (p));
  q = abs (p);
  return vec3 (p, (SQRT3/2.) - q.x + 0.5 * min (q.x - SQRT3 * q.y, 0.));
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 vn, col, gCol, qh;
  float sd, f;
  vec2 e = vec2 (0.01, 0.);
  if (rd.y > 0.) {
    ro.xz += 2. * tCur;
    sd = max (dot (rd, sunDir), 0.);
    col = vec3 (0.1, 0.2, 0.4) + 0.2 * (1. - max (rd.y, 0.)) +
       0.1 * pow (sd, 16.) + 0.2 * pow (sd, 256.);
    f = Fbm2 (0.02 * (ro.xz + rd.xz * (100. - ro.y) / max (rd.y, 0.01)));
    col = mix (col, vec3 (1.), clamp (0.2 + 0.8 * f * rd.y, 0., 1.));
  } else {
    ro -= (ro.y / rd.y) * rd;
    qh = HexGrid (8. * ro.xz);
    f = max (length (qh.xy) - 0.5, 0.);
    vn = vec3 (0., Rot2D (vec2 (1., 0.), 5. * f * f));
    vn.zx = vn.z * vec2 (qh.x, - qh.y) / length (qh.xy);
    vn = VaryNf (50. * ro, vn, 0.5);
    gCol = vec3 (0.8, 0.9, 0.8);
    col = gCol * (0.5 + 0.5 * smoothstep (0.03, 0.06, qh.z)) *
       (1. - 0.1 * Noisefv2 (100. * ro.xz));
    col = col * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) +
       0.2 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 128.);
    col = mix (mix (col, 0.7 * gCol, pow (1. + rd.y, 4.)), 0.95 * vec3 (0.45, 0.55, 0.7),
       pow (1. + rd.y, 64.));
  }
  return col;
}

float MirHit (vec3 ro, vec3 rd)
{
  vec3 v;
  float h, b, d;
  v = ro - msOrg;
  b = dot (rd, v);
  d = b * b + msRad * msRad - dot (v, v);
  h = dstFar;
  if (d > 0.) {
    d = sqrt (d);
    h = - b + d * msDir;
    qHit = ro + h * rd;
    v = qHit - msOrg;
    if (qHit.z > 0. || length (v.xy) > mDskRad) h = dstFar;
    else qnHit = v / msRad;
  }
  return h;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 roo, rdo, col, vn, colA[2];
  float dstObj, dstMir, sh, spec;
  bool isBg;
  dstMir = (rd.z > 0.) ? MirHit (ro, rd) : dstFar;
  roo = ro;
  rdo = rd;
  for (int k = 0; k <= 1; k ++) {
    onRob = (k == 0);
    ro = roo;
    rd = rdo;
    isRefl = false;
    isBg = false;
    dstObj = ObjRay (ro, rd);
    if (dstMir < min (dstObj, dstFar)) {
      rd = reflect (rd, qnHit);
      ro = qHit;
      isRefl = true;
      dstObj = ObjRay (ro, rd);
      if (dstObj >= dstFar) isBg = true;
    } else if (dstObj >= dstFar) isBg = true;
    if (! isBg) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      if (idObj == 1) {
        vn = VaryNf (100. * ro, vn, 1.);
        col = 0.4 * vec3 (1., 0.8, 0.2) + 0.6 * BgCol (ro, reflect (rd, vn));
        spec = 0.3;
      } else if (idObj == 2) {
        vn = VaryNf (100. * ro, vn, max (5. - 7. * growPhs, 0.1));
        col = mix (vec3 (0.3, 0.15, 0.15), mix (vec3 (0.2, 1., 0.2), vec3 (0.1, 0.3, 0.8),
           clamp (5. * growPhs - 3., 0., 1.)), growPhs);
        spec = 0.1 + 0.3 * growPhs;
      } else if (idObj == 3) {
        col = vec3 (1., 1., 0.);
        spec = 1.;
      }
      sh = ObjSShadow (ro, sunDir);
      col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
         spec * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 128.);
    } else {
      col = BgCol (ro, rd);
      if (rd.y < 0.) {
        ro -= (ro.y / rd.y) * rd;
        col *= ObjSShadow (ro, sunDir);
      }
    }
    colA[k] = col;
  }
  col = mix (colA[0], colA[1], 1. - SmoothBump (0.1, 0.8, 0.1, tCyc)) *
     mix (vec3 (0.9, 0.6, 0.6), vec3 (1.), SmoothBump (0.15, 0.9, 0.1, tCyc));
  return pow (clamp (col, 0., 1.), vec3 (0.8));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec3 ro, rd, vd;
  vec2 canvas, uv;
  float az, el, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  dstFar = 15.;
  t = mod (0.08 * tCur, 2.);
  msDir = 2. * floor (t) - 1.;
  t = pow (0.5 * (1. + cos (2. * pi * t)), 2.);
  msRad = exp (8. * t) + 0.4;
  mDskRad = 0.75;
  frmWid = 0.05;
  msDep = 0.25;
  msOrg = vec3 (0., mDskRad + frmWid,
     - sqrt (msRad * msRad - mDskRad * mDskRad) * msDir - msDep);
  el = 0.03 * pi;
  az = 0.;
  if (mPtr.z > 0.) {
    el = clamp (el - 2. * mPtr.y, 0.02 * pi, 0.3 * pi);
    az = clamp (az - 2. * mPtr.x, -0.3 * pi, 0.3 * pi);
  } else az += 0.2 * pi * sin (0.011 * 2. * pi * tCur);
  ro = vec3 (0., mDskRad + frmWid, -7.);
  rd = normalize (vec3 (uv, 4.));
  ro.yz = Rot2D (ro.yz, el);
  rd.yz = Rot2D (rd.yz, el);
  ro.xz = Rot2D (ro.xz, az);
  rd.xz = Rot2D (rd.xz, az);
  tCyc = mod (0.03 * tCur, 1.);
  growPhs = SmoothBump (0.3, 0.8, 0.15, tCyc);
  t = mod (0.3 * tCur, 1.);
  rAngH = 0.2 * pi * sin (2. * pi * t);
  rAngA = 2. * pi * (0.5 - abs (t - 0.5));
  rAngSh = 0.1 * (rAngA - 0.5 * pi);
  t = smoothstep (0.85, 1., growPhs);
  rAngH *= t;
  rAngA *= t;
  rAngSh *= t;
  sunDir = normalize (vec3 (1., 2. - 3.2 * abs (tCyc - 0.5), -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrRndBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRCylDf (vec3 p, float r, float rt, float h)
{
  vec2 dc;
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  dc = vec2 (dxy, dz) + rt;
  return min (min (max (dc.x, dz), max (dc.y, dxy)), length (dc) - rt);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f;
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 e = vec3 (0.1, 0., 0.);
  float s = Fbmn (p, n);
  vec3 g = vec3 (Fbmn (p + e.xyy, n) - s,
     Fbmn (p + e.yxy, n) - s, Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}
