// volumetric variant of  https://shadertoy.com/view/XlBBRR

#define rndB(U) texture(iChannel0, U)     // trilinear interpolation

vec4 rndC(vec3 u) {                       // tricubic interpolation ( 1 single fetch: thanks iq :-) )
    vec3 R = iChannelResolution[0].xyz,
         U = u*R + .5,
         F = fract(U);
    U = floor(U) + F*F*(3.-2.*F); 
 // U = floor(U) + F*F*F*(F*(F*6.-15.)+10.);   // use if you want smooth gradients
    return texture( iChannel0, (U-.5) / R );
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U = ( U+U - R ) / R.y;
    vec3 V = .2*vec3(U,.1*iTime);

	O =   U.y > 2./R.y ? rndC(V)          // tricubic (null-tangent)
        : U.y > 0.     ? vec4(14) 
        :                rndB(V);         // trilinear
    if (U.x>0.) O = sin(10.*O);
  //O = sqrt(O);                          // gamma correction
}


