vec3 b( vec2 p, float f, vec3 r, vec4 mt2 )
{
    float res1;
    vec3 res3;
    vec3 x = smoothstep(vec3(f), r/2.0, r/5.0);
    float i = smoothstep(max(p.x-length(mt2.xy), p.y-length(mt2.zw)), f/2.0, f/5.0);
    vec3 mt3 = normalize(vec3(p, i));
    vec4 v2 = vec4(i-f);
        float t = 0.0;
        for(float hr = 0.0; hr < 26.072; hr += 0.001)
        {
            float pf = floor(fract( length(x))-f);
            vec3 mt4 = normalize(vec4(max(mt3, x), 1.0)).xyz - length(pf);
            float lrt = length(mt4);
            t += lrt * 0.5;
            res3 += mt4 * t;
        }
        res1 += t * length(v2);
    return res3 - res1;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    float f = length(fragCoord);
    vec3 r = vec3(uv, 1.0);
    vec4 mt2 = vec4(uv, uv);
	fragColor = texture(iChannel0, b(uv, f, r, mt2).xy);
}
