vec3 toVec3( vec4 v ) {
    if( IS_ZERO( v.w ) ) {
        return v.xyz;
    }

    return v.xyz*(1.0/v.w);
}

vec2 rotateVec(vec2 v, float angle) {
    vec2 cs = vec2(cos(angle), sin(angle));
    return vec2(dot(v, cs), dot(v, vec2(cs.y, -cs.x)));
}

mat3 VToRMat (vec3 v, float a)
{
  mat3 m;
  float c, s, a1, a2;
  c = cos (a);  s = sin (a);
  m[0][0] = (1. - c) * v.x * v.x + c;
  m[1][1] = (1. - c) * v.y * v.y + c;
  m[2][2] = (1. - c) * v.z * v.z + c;
  a1 = (1. - c) * v.x * v.y;  a2 = - s * v.z;
  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = (1. - c) * v.z * v.x;  a2 = - s * v.y;
  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = (1. - c) * v.y * v.z;  a2 = - s * v.x;
  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return m;
}

mat4 translateMtx(vec3 p) {
    mat4 t;
    t[0] = vec4(1.0, 0.0, 0.0, 0.0);
    t[1] = vec4(0.0, 1.0, 0.0, 0.0);
    t[2] = vec4(0.0, 0.0, 1.0, 0.0);
    t[3] = vec4(p, 1.0);
    return t;
}

//build rotation matrix around point p and vector v of angle a
mat4 VToRMat (vec3 p, vec3 v, float a)
{
    mat4 m;
    float c, s, a1, a2;
    c = cos (a); 
    s = sin (a);
    m[0][0] = (1. - c) * v.x * v.x + c;
    m[1][1] = (1. - c) * v.y * v.y + c;
    m[2][2] = (1. - c) * v.z * v.z + c;
    a1 = (1. - c) * v.x * v.y;
    a2 = - s * v.z;
    m[0][1] = a1 + a2;
    m[1][0] = a1 - a2;
    a1 = (1. - c) * v.z * v.x;
    a2 = - s * v.y;
    m[2][0] = a1 + a2;
    m[0][2] = a1 - a2;
    a1 = (1. - c) * v.y * v.z;
    a2 = - s * v.x;
    m[1][2] = a1 + a2;
    m[2][1] = a1 - a2;

    m[0][3] = 0.0;
    m[1][3] = 0.0;
    m[2][3] = 0.0;
    m[3][0] = 0.0;
    m[3][1] = 0.0;
    m[3][2] = 0.0;
    m[3][3] = 1.0;

    return translateMtx(p) * m * translateMtx(-p);
}

vec3 orthogonalize(in vec3 n, in vec3 v) {
    return v - n * dot(n, v);
}

mat3 rotAroundX(float alpha) {
    mat3 mtx;
    mtx[0] = vec3(1.0,         0.0,        0.0);
    mtx[1] = vec3(0.0,  cos(alpha), sin(alpha));
    mtx[2] = vec3(0.0, -sin(alpha), cos(alpha));
    return mtx;
}

mat3 rotAroundY(float alpha) {
    mat3 mtx;
    mtx[0] = vec3(cos(alpha), 0.0, -sin(alpha));
    mtx[1] = vec3(       0.0, 1.0,         0.0);
    mtx[2] = vec3(sin(alpha), 0.0,  cos(alpha));
    return mtx;
}

mat4 fromPosRot(in vec3 p, in mat3 r) {
    mat4 mtx;
    mtx[0] = vec4(r[0], 0.0);
    mtx[1] = vec4(r[1], 0.0);
    mtx[2] = vec4(r[2], 0.0);
    mtx[3] = vec4(p, 1.0);
    return mtx;
}

vec3 slerp(vec3 a, vec3 b, float t) {
	float theta = acos(dot(a, b));
    return 	a * sin((1.0 - t)*theta) / sin(theta) +
    		b * sin(t*theta) / sin(theta);
}

mat4 mtxLerp(in mat4 a, in mat4 b, float p) {
    mat4 mtx = a;
    for(int i=0; i<3; i++) {
    	vec3 rot_vec = normalize(cross(a[i].xyz, b[i].xyz));
        float rot_angle = acos(dot(a[i].xyz, b[i].xyz));
        mat4 rot = VToRMat(vec3(0.), -rot_vec, p * rot_angle);
        mtx = rot * mtx;
    }
    mtx[3].xyz = mix(a[3].xyz, b[3].xyz, p);
    return mtx;
}

mat4 mtxLerp2(in mat4 a, in mat4 b, float p) {
    mat4 mtx = a;
    mtx[0].xyz = normalize(mix(a[0].xyz, b[0].xyz, p));
    mtx[1].xyz = normalize(mix(a[1].xyz, b[1].xyz, p));
    mtx[2].xyz = normalize(mix(a[2].xyz, b[2].xyz, p));
    mtx[1].xyz = orthogonalize(mtx[0].xyz, mtx[1].xyz);
    mtx[2].xyz = orthogonalize(mtx[0].xyz, mtx[2].xyz);
    mtx[2].xyz = orthogonalize(mtx[1].xyz, mtx[2].xyz);
    mtx[3].xyz = mix(a[3].xyz, b[3].xyz, p);
    return mtx;
}

#if __VERSION__ >= 300
mat3 mat3Inverse( in mat3 m ) { return inverse(m); }
mat4 mat4Inverse( in mat4 m ) {return inverse(m);}
#else
mat3 mat3Inverse( in mat3 m ) {
    return mat3(	vec3( m[0][0], m[1][0], m[2][0] ),
					vec3( m[0][1], m[1][1], m[2][1] ),
                    vec3( m[0][2], m[1][2], m[2][2] ) );
}

//fast inverse for orthogonal matrices
mat4 mat4Inverse( in mat4 m ) {
    mat3 rotate_inv = mat3(	vec3( m[0][0], m[1][0], m[2][0] ),
                          	vec3( m[0][1], m[1][1], m[2][1] ),
                          	vec3( m[0][2], m[1][2], m[2][2] ) );

    return mat4(	vec4( rotate_inv[0], 0.0 ),
                	vec4( rotate_inv[1], 0.0 ),
                	vec4( rotate_inv[2], 0.0 ),
              		vec4( (-rotate_inv)*m[3].xyz, 1.0 ) );
}
#endif

mat4 createCS(vec3 p, vec3 z, vec3 x) {
    z = normalize(z);
    x = normalize(orthogonalize(z, x));
    vec3 y = cross(z,x);

    return mat4(	vec4( x, 0.0 ), 
    			 	vec4( y, 0.0 ),
    				vec4( z, 0.0 ),
    				vec4( p, 1.0 ));
}



void saveBall(in Ball ball, int id, in ivec2 p, inout vec4 fragColor) {
    for(int i=0; i<4; i++) {
    	fragColor = (p.y == id && p.x == i)? ball.mtx[i] : fragColor;
    }
#ifdef MOTION_BLUR
    for(int i=0; i<4; i++) {
    	fragColor = (p.y == id && p.x == 4+i)? ball.mtx_prev[i] : fragColor;
    }
#endif
    fragColor = (p.y == id && p.x == 8)? vec4(0.0, ball.v) : fragColor;
}

Ball loadBall(in sampler2D tex, in int id) {
    Ball ball;
    for(int i=0; i<4; i++) {
        ball.mtx[i] = texelFetch(tex, ivec2(i, id), 0);
    }
#ifdef MOTION_BLUR
    for(int i=0; i<4; i++) {
        ball.mtx_prev[i] = texelFetch(tex, ivec2(4+i, id), 0);
    }
#endif
    vec4 tmp = texelFetch(tex, ivec2(8, id), 0);
    ball.v   = tmp.yzw;
    return ball;
}

void saveCue(in Cue cue, in ivec2 p, inout vec4 fragColor) {
    vec4 fragColorOld = fragColor;

    fragColor = mix(fragColor, cue.mtx[0], vec4(p.y == 50));
    fragColor = mix(fragColor, cue.mtx[1], vec4(p.y == 51));
    fragColor = mix(fragColor, cue.mtx[2], vec4(p.y == 52));
    fragColor = mix(fragColor, cue.mtx[3], vec4(p.y == 53));
	fragColor = mix(fragColor, vec4(cue.r0, cue.r1, cue.h, 0.0), vec4(p.y == 54));

    fragColor = mix(fragColorOld, fragColor, vec4(p.x == 0));
}

Cue readCue(in sampler2D tex){
    Cue cue;
    cue.mtx[0] = texelFetch(tex, ivec2(0, 50), 0);
    cue.mtx[1] = texelFetch(tex, ivec2(0, 51), 0);
    cue.mtx[2] = texelFetch(tex, ivec2(0, 52), 0);
    cue.mtx[3] = texelFetch(tex, ivec2(0, 53), 0);
    vec4 a = texelFetch(tex, ivec2(0, 54), 0);
    cue.r0 = a.x; 
    cue.r1 = a.y;
    cue.h = a.z;
    return cue;
}

void saveCamera(in Camera camera, in ivec2 p, inout vec4 fragColor) {
    vec4 fragColorOld = fragColor;

    fragColor = mix(fragColor, camera.mtx[0], vec4(p.y == 60));
    fragColor = mix(fragColor, camera.mtx[1], vec4(p.y == 61));
    fragColor = mix(fragColor, camera.mtx[2], vec4(p.y == 62));
    fragColor = mix(fragColor, camera.mtx[3], vec4(p.y == 63));
    fragColor = mix(fragColor, camera.mtx_prev[0], vec4(p.y == 64));
    fragColor = mix(fragColor, camera.mtx_prev[1], vec4(p.y == 65));
    fragColor = mix(fragColor, camera.mtx_prev[2], vec4(p.y == 66));
    fragColor = mix(fragColor, camera.mtx_prev[3], vec4(p.y == 67));
	fragColor = mix(fragColor, vec4(camera.fovV, camera.lensSize, camera.focusDist, 0.), vec4(p.y == 68));

    fragColor = mix(fragColorOld, fragColor, vec4(p.x == 0));
}

Camera readCamera(in sampler2D tex) {
    Camera c;
    c.mtx[0] = texelFetch(tex, ivec2(0, 60), 0);
    c.mtx[1] = texelFetch(tex, ivec2(0, 61), 0);
    c.mtx[2] = texelFetch(tex, ivec2(0, 62), 0);
    c.mtx[3] = texelFetch(tex, ivec2(0, 63), 0);
