vec4 char(vec2 pos, float c) {
    pos = clamp(pos,0.,1.);  // would be more efficient to exit if out.
    vec4 tx= texture( iChannel3, pos/16. + fract( floor(vec2(c, 15.999-c/16.)) / 16. ) )*2. ;
    vec4 ty= texture( iChannel3, pos/16.012 + fract( floor(vec2(c, 15.999-c/16.012)) / 16.012 ) )/3.0 ;
    vec4 tz= texture( iChannel2,pos);
    return  vec4((tx+ty)/(tz*3.0));
}

vec3 getBallTexture(vec2 uv, vec3 color, int num) {
    vec3 white = vec3(1.0, 1.0, 0.8);
    if(num == 0) {
        return white;
    } else {
        bool solid = (num < 9);
        float edgeBlend = 0.01;
        vec2 dirToCenter = vec2(0.5, 0.5) - vec2(uv.x + (0.5-uv.x)*0.5, uv.y);
        float d = sqrt(dot(dirToCenter, dirToCenter));
        float r = 0.07;
        vec3 res;

        float dirt = texture(iChannel2,uv*1.5).x;

        vec3 non_solid;
        float dd = abs(uv.y - 0.5) * 2.0f;
        if(dd < 0.18 - edgeBlend) {
            non_solid = color;
        } else if(dd > 0.18 + edgeBlend) {
            non_solid = white;
        } else {
            float blende = ((dd - (0.18 - edgeBlend))/(2.0*edgeBlend))*dirt;
            non_solid = mix(color, white, blende);
        }
        vec3 outside_circle = mix(non_solid, color, solid? 1.0 : 0.0);
        vec3 inside_circle = vec3(0.);
        vec2 scale = vec2(5.0, 8.0);
        if(num > 9) {
            vec4 numc1 = char(scale*(uv - 0.5) + vec2(0.3, 0.5), 48.0 + float(num-10));
            vec4 numc2 = char(scale*(uv - 0.5) + vec2(0.7, 0.5), 48.0 + float(num/10));
            numc1.xyz = vec3(1.0) - numc1.xxx*2.0;
            numc2.xyz = vec3(1.0) - numc2.xxx*2.0;
            inside_circle = mix(white, numc1.xyz, numc1.w)*mix(white, numc2.xyz, numc2.w);
        } else {
            vec4 numc = char(scale*(uv - 0.5) + vec2(0.5), 48.0 + float(num));
            numc.xyz = vec3(1.0) - numc.xxx*2.0;
            inside_circle = mix(white, numc.xyz, numc.w);
        }

        bool on_69_mark = (d > 0.047) && (d < 0.057) && (uv.y < 0.5) && (abs(uv.x - 0.5) < 0.03);
        inside_circle *= vec3((num == 6 || num == 9)? (on_69_mark? dirt*dirt : 1.0) : 1.0);
        res = mix(outside_circle, inside_circle, (d < r - edgeBlend)?1.0 : 0.0);

        float blendc = ((d - (r - edgeBlend))/(2.0*edgeBlend))*dirt;
        vec3 on_the_circle = mix(white, color, blendc);
        res = mix(res, on_the_circle, (abs(d - r) < edgeBlend)?1.0 : 0.0);
        return res;
    }
}

void initBalls() {
    const int rack_pattern[15] = int[](1, 2, 9, 10, 8, 3, 4, 14, 7, 11, 12, 6, 15, 13, 5);
    int b = 0;
    for(int i=0; i<5; i++) {
        for(int j=0; j<=i; j++) {
            balls[rack_pattern[b]].v = vec3(0.);
            balls[rack_pattern[b]].mtx = createCS(vec3(-12.0 - float(i)*2.0, 0.0, float(i) - float(j)*2.0),
                                    vec3(rnd(), rnd(), 0.0),
                                    vec3(0.0, rnd(), rnd()));
            b++;
    	}
    }

    balls[0].v = vec3(0.);
    balls[0].mtx = createCS(vec3(14.0, 0.0, 0.0),
                            vec3(1.0, 0.0, 0.0),
                            vec3(0.0, 0.0, 1.0));

#ifdef MOTION_BLUR
    for(int i=0; i<numBalls; i++) {balls[i].mtx_prev = balls[i].mtx;}
#endif
}


void initCamera(in vec3 pos, in vec3 target, in float fovV, in float radius, in float focus_dist) {
	const vec3 upDir = vec3(0.0, -1.0, 0.0);	
	vec3 back = normalize( pos-target );
	vec3 right = normalize( cross( upDir, back ) );
	vec3 up = cross( back, right );
    camera.mtx[0] = vec4(right, 0.0);
    camera.mtx[1] = vec4(up, 0.0);
    camera.mtx[2] = vec4(back, 0.0);
    camera.mtx[3] = vec4(pos, 1.0);
    camera.mtx_prev = camera.mtx;
    camera.fovV = fovV;
    camera.focusDist = focus_dist;
    camera.lensSize = radius;
}

