    }
}

void updateball(int ballid) {
    vec3 pos = balls[ballid].mtx[3].xyz;
    float speed = length(balls[ballid].v);
    //if(speed > 0.001) {
    bool is_moving = (speed > 0.0);
    for(int j=0; j<numBalls; j++) {
        if(ballid != j) {
            vec3 pos2 = balls[j].mtx[3].xyz;
            vec3 vec = pos2 - pos;
            float dist2 = dot(vec, vec);
            if(dist2 < 4.0) {
                vec  = normalize(vec) * (2.0 - sqrt(dist2));
                balls[j].v += vec*0.46;
                balls[ballid].v += -vec*0.46;
                is_moving = true;
            }
        }
    }

    if(is_moving) {
        speed = length(balls[ballid].v);//update speed
        vec3 dir = normalize(balls[ballid].v);
        vec3 dir_new = dir;
        vec3 new_pos = pos + dir * speed;


        if(new_pos.x > tablemax.x - 1.0) {
            new_pos.x = tablemax.x - 1.0;//this is wrong
            dir_new = reflect(dir, vec3(1.0, 0.0, 0.0));
            speed *= 0.9;
        }

        if(new_pos.x < tablemin.x + 1.0) {
            new_pos.x = tablemin.x + 1.0;//this is wrong
            dir_new = reflect(dir, vec3(-1.0, 0.0, 0.0));
            speed *= 0.9;
        }

        if(new_pos.z > tablemax.y - 1.0) {
            new_pos.z = tablemax.y - 1.0;//this is wrong
            dir_new = reflect(dir, vec3(0.0, 0.0, -1.0));
            speed *= 0.9;
        }

        if(new_pos.z < tablemin.y + 1.0) {
            new_pos.z = tablemin.y + 1.0;//this is wrong
            dir_new = reflect(dir, vec3(0.0, 0.0, 1.0));
            speed *= 0.9;
        }

        float angle = speed;
        vec3 rot_vec = cross(vec3(0.0, 1.0, 0.0), dir);
        mat4 rot = VToRMat(vec3(0.), rot_vec, -angle);
        mat4 new_mtx = rot * balls[ballid].mtx;
        new_mtx[3].xyz = new_pos;
#ifdef MOTION_BLUR
        balls[ballid].mtx_prev = balls[ballid].mtx;
#endif
        balls[ballid].mtx = new_mtx;
        balls[ballid].v = dir_new * sqrt(speed*speed * 0.97);
    }
}

void updateCueAndCamera() {
	vec3 avg_pos = vec3(0.);
    for(int i=0; i<numBalls; i++) {
    	avg_pos += balls[i].mtx[3].xyz;
    }
    avg_pos /= float(numBalls);


    if(length(balls[0].v) < 0.03) {
        vec3 cue_pos = cue.mtx[3].xyz;
        vec3 cue_dir = cue.mtx[2].xyz;
        vec3 white_ball_pos = balls[0].mtx[3].xyz;

        vec3 desired_cue_dir = normalize(normalize(avg_pos-white_ball_pos)/* + vec3(0.0, -0.1, 0.0)*/);

        //mx from -1 to 1
        float mx = (iMouse.x==0.0) ? 0.0 : ((iMouse.x / iResolution.x)*2.0 - 1.0);
       	desired_cue_dir = rotAroundY(mx*2.0) * desired_cue_dir;
        vec3 desired_cue_pos = white_ball_pos - cue_dir * (cue.h + 3.0 + sin(iTime*5.0)*3.0);
        vec3 target = mix(avg_pos, white_ball_pos + cue_dir * (cue.h + 20.0), 0.5);

		float rot_angle = acos(dot(cue_dir, desired_cue_dir));
        if(rot_angle > 0.005) {
            vec3 rot_vec = normalize(cross(cue_dir, desired_cue_dir));
            vec3 rot_pos = cue_pos + cue_dir * cue.h;
            mat4 rot = VToRMat(rot_pos, -rot_vec, 0.05*rot_angle);
            cue.mtx = rot * cue.mtx;
        }

        cue.mtx[3].xyz = mix(desired_cue_pos, cue.mtx[3].xyz, 0.96);
        //just to make it orthogonal again
        cue.mtx = createCS(	cue.mtx[3].xyz,
                            cue.mtx[2].xyz,
                            cue.mtx[0].xyz);

        float my = iMouse.y / iResolution.y;
        vec3 left = normalize(cross(cue_dir, vec3(0.0, -1.0, 0.0)));
        vec3 desired_cam_pos = white_ball_pos - cue_dir * cue.h * (0.2 + my) + vec3(0., 3.0 + 10.0 * my, 0.) + left * 5.0 * my;
        vec3 desired_cam_dir = -normalize(target - desired_cam_pos);
        mat4 desired_mtx = createCS(desired_cam_pos,
                                    desired_cam_dir,
                                    normalize(cross(vec3(0.0, -1.0, 0.0), desired_cam_dir)));

        camera.mtx_prev = camera.mtx;
        camera.mtx = mtxLerp2(camera.mtx, desired_mtx, 0.2);
    }
}

