void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    ivec2 p = ivec2(fragCoord);
    if(int(fragCoord.x) < 16 && int(fragCoord.y) < 128 ) {

        if(iFrame == 0) {
            initBalls();
            initCue();
            vec3 cameraTarget = vec3(0., 0., -4.);
            vec3 camDir = vec3(1.0, 0.2, 0.0);
            float cameraDist = 40.0;
            initCamera(cameraTarget + camDir*cameraDist, cameraTarget, 18.0, LENS_SIZE, cameraDist);
            if(p.y < numBalls) {
                int ballid = p.y;
            	saveBall(balls[ballid], ballid, p, fragColor);
            }
            //saveBalls(p, fragColor);
            saveCue(cue, p, fragColor);
            saveCamera(camera, p, fragColor);
        } else {

            loadBalls();
            cue = readCue(iChannel0);
            camera = readCamera(iChannel0);

            if( (texelFetch( iChannel1, ivec2(32,0),0 ).x > 0.0 &&
                texelFetch( iChannel1, ivec2(32,2),0 ).x == 0.0) &&
                length(balls[0].v) < 0.01){
                vec3 cue_dir = cue.mtx[2].xyz;
                balls[0].v = vec3(cue_dir.x, 0.0, cue_dir.z) * 2.0;
            }

            if(p.y < numBalls) {
                int ballid = p.y;
            	updateball(ballid);
                saveBall(balls[ballid], ballid, p, fragColor);
            }
            updateCueAndCamera();
			//initCamera(vec3(-17.0, 10.0, 0.0), vec3(-17.2, 0.0, 0.0), 18.0, LENS_SIZE, 10.0);
            saveCue(cue, p, fragColor);
            saveCamera(camera, p, fragColor);

        }

        updateAABB(p, fragColor);
    } else if(int(fragCoord.y) >= 128 && int(fragCoord.y) < 256 && int(fragCoord.x) < 512) {//ball textures
        if(iFrame < 10) {
            int x = int(fragCoord.x);
            int y = int(fragCoord.y) - 128;
            int ballid = x / 64 + (y / 64)*8;
            ballid = clamp(ballid, 0, 16);
            vec2 uv = vec2(mod(fragCoord.x, 64.0f) / 64.0f, mod(fragCoord.y, 64.0f) / 64.0f);

            uv -= vec2(0.5, 0.5);
            uv /= vec2(2.0f, 4.0f);
            uv += vec2(0.5, 0.5);
            vec3 c = getBallTexture(uv, ballcolors[ballid], ballid);
            fragColor = vec4(c, 1.);
        } else {
            fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
        }
    } else {
        discard;
        //fragColor = vec4(0.);
    }
}

