vec3 getBallTexture(vec2 uv, vec3 color, int num) {
    uv = vec2(1.0 - uv.y, uv.x);
    uv -= vec2(0.5, 0.5);
    uv *= vec2(4.0f, 2.0f);
    uv += vec2(0.5, 0.5);
    uv = min(uv, vec2(0.97));
    uv = max(uv, vec2(0.03));

    int px = (num > 7 ? num - 8 : num) * 64 + int(uv.x * 64.0);
    int py = 128 + (num > 7 ? 1 : 0) * 64 + int(uv.y * 64.0);
    uv = vec2(float(px), float(py)) / iResolution.xy;
    return texture(iChannel0,uv).xyz;
}

vec3 getCueTexture(vec2 uv) {
    vec3 wood = texture( iChannel1, uv.yx ).xyz;

    if(uv.y > 1.0) {
        return wood;
    } else {
    	float k = fract(uv.x / 0.2);
    	float h = 0.3;
    	float a = mix(-1.0, 1.0, float(k < h));
    	return (a*(h-k)*0.3 < uv.y-h)?wood:vec3(0.01);
    }
}

vec3 getFabricColor(vec2 uv) {
    float aa = texture(iChannel1,uv).r*0.5 + texture(iChannel1,uv.yx).r*0.5;//add some more inperfection
	return vec3(0.3, 0.9, 0.2)*(aa*0.5 + 0.2)*0.6;
}  

vec3 getColor(vec2 uv, int tex) {
    vec3 c;
    if(tex==0) c = texture( iChannel0, uv ).xyz; else
    if(tex==1) c = texture( iChannel1, uv ).xyz; else
    if(tex==2) c = texture( iChannel2, uv ).xyz; else
    if(tex==3) c = texture( iChannel3, uv ).xyz; else
    if(tex==4) c = getCueTexture(uv); else
    if(tex==5) c = getFabricColor(uv); else
    if(tex > 5) {
        int ballid = tex - 6;
    	c = getBallTexture(uv, ballcolors[ballid], ballid);
    }
    return clamp(c, 0.0, 1.0);
}

