// Geometry functions ***********************************************************
vec2 uniformPointWithinCircle( in float radius, in float Xi1, in float Xi2 ) {
    float r = radius*sqrt(1.0 - Xi1);
    float theta = Xi2*TWO_PI;
	return vec2( r*cos(theta), r*sin(theta) );
}

float copysignf(in float x, in float y){
     uint ix = floatBitsToUint(x);
     uint iy = floatBitsToUint(y);
     return uintBitsToFloat((ix&uint(0x7fffffff))|(iy&uint(0x80000000)));
}

void branchlessONB(vec3 n, out vec3 b1, out vec3 b2){
    float sign = copysignf(1.0f, n.z);
    float a = -1.0f / (sign + n.z);
    float b = n.x * n.y * a;
    b1 = vec3(1.0f + sign * n.x * n.x * a, sign * b, -sign * n.x);
    b2 = vec3(b, sign + n.y * n.y * a, -n.y);
}

mat3 mat3FromNormal(in vec3 n) {
    vec3 x, y;
    branchlessONB(n, x, y);
    return mat3(x,y,n);
}

void cart2sph(in vec3 xyz, out float rho, out float phi,out float theta) {
    rho = sqrt((xyz.x * xyz.x) + (xyz.y * xyz.y) + (xyz.z * xyz.z));
    phi = asin(xyz.y / rho);
	theta = atan( xyz.z, xyz.x );
}

vec3 sph2cart( in float rho, in float phi, in float theta ) {
    float sinTheta = sin(theta);
    return vec3( sinTheta*cos(phi), sinTheta*sin(phi), cos(theta) )*rho;
}

vec3 sampleHemisphereCosWeighted( in float Xi1, in float Xi2 ) {
    float theta = acos(clamp(sqrt(1.0-Xi1),-1.0, 1.0));
    float phi = TWO_PI * Xi2;
    return sph2cart( 1.0, phi, theta );
}
vec3 sphericalToCartesian(	in float rho, in float phi, in float theta ) {
    float sinTheta = sin(theta);
    return vec3( sinTheta*cos(phi), sinTheta*sin(phi), cos(theta) )*rho;
}

vec3 randomHemisphereDirection( const vec3 n, in float Xi1, in float Xi2, out float pdf ) {
    pdf = 1.0 / (2.0 * PI);
    vec2 r = vec2(Xi1,Xi2)*TWO_PI;
	vec3 dr=vec3(sin(r.x)*vec2(sin(r.y),cos(r.y)),cos(r.x));
	return dot(dr,n) * dr;
}

vec3 randomDirection( in float Xi1, in float Xi2,out float pdf ) {
    pdf = 1.0 / (4.0 * PI);
    float theta = acos(1.0 - 2.0*Xi1);
    float phi = TWO_PI * Xi2;

    return sphericalToCartesian( 1.0, phi, theta );
}
