Ray genRay(Camera c, in vec2 uv, float ratio, in float Xi1, in float Xi2, in float time ) {
    Ray ray;
    ray.time = time;

    vec2 iPlaneSize=2.*tan(0.5*c.fovV)*vec2(iResolution.x/iResolution.y,1.);
	vec2 ixy=(uv - 0.5)*iPlaneSize;

    mat4 mtx;
#ifdef MOTION_BLUR
    mtx = mtxLerp2(c.mtx_prev, c.mtx, ray.time);
#else
    mtx = c.mtx;
#endif

    if( c.lensSize > EPSILON ) {
        vec2 uv = uniformPointWithinCircle( c.lensSize, rnd(), rnd() );
        vec3 newPos = mtx[3].xyz + mtx[0].xyz*uv.x*c.lensSize + mtx[1].xyz*uv.y*c.lensSize;
        vec3 focusPoint = mtx[3].xyz - c.focusDist*mtx[2].xyz;
        vec3 newBack = normalize(newPos - focusPoint);
        vec3 newRight = normalize( cross( mtx[1].xyz, newBack ) );
        vec3 newUp = cross( newBack, newRight );
        mat3 newRotate;
        newRotate[0] = newRight;
        newRotate[1] = newUp;
        newRotate[2] = newBack;

        ray.origin = newPos;
        ray.dir = newRotate*normalize(vec3(ixy.x,ixy.y,-1.0));
    } else {
        ray.origin = mtx[3].xyz;
        ray.dir = toVec3(mtx*normalize(vec4(ixy.x, ixy.y, -1.0, 1.0)));
    }

	return ray;
}

#ifdef SPHERICAL_RECTANGLE
struct SphQuad {
    vec3 o, x, y, z; // local reference system R
    float z0, z0sq; //
    float x0, y0, y0sq; // rectangle coords in R
    float x1, y1, y1sq; //
    float b0, b1, b0sq, k; // misc precomputed constants
    float S; // solid angle of Q
};

void SphQuadInit(vec3 s,vec3 ex,vec3 ey,vec3 o, out SphQuad squad) {
    squad.o = o;
    float exl = length(ex), eyl = length(ey);
    // compute local reference system R
    squad.x = ex / exl;
    squad.y = ey / eyl;
    squad.z = cross(squad.x, squad.y);
    // compute rectangle coords in local reference system
    vec3 d = s - o;
    squad.z0 = dot(d, squad.z);
    // flip z to make it point against Q
    if (squad.z0 > 0.) {
    squad.z *= -1.;
    squad.z0 *= -1.;
    }
    squad.z0sq = squad.z0 * squad.z0;
    squad.x0 = dot(d, squad.x);
    squad.y0 = dot(d, squad.y);
    squad.x1 = squad.x0 + exl;
    squad.y1 = squad.y0 + eyl;
    squad.y0sq = squad.y0 * squad.y0;
    squad.y1sq = squad.y1 * squad.y1;
    // create vectors to four vertices
    vec3 v00 = vec3(squad.x0, squad.y0, squad.z0);
    vec3 v01 = vec3(squad.x0, squad.y1, squad.z0);
    vec3 v10 = vec3(squad.x1, squad.y0, squad.z0);
    vec3 v11 = vec3(squad.x1, squad.y1, squad.z0);
    // compute normals to edges
    vec3 n0 = normalize(cross(v00, v10));
    vec3 n1 = normalize(cross(v10, v11));
    vec3 n2 = normalize(cross(v11, v01));
    vec3 n3 = normalize(cross(v01, v00));
    // compute internal angles (gamma_i)
    float g0 = acos(-dot(n0,n1));
    float g1 = acos(-dot(n1,n2));
    float g2 = acos(-dot(n2,n3));
    float g3 = acos(-dot(n3,n0));
    // compute predefined constants
    squad.b0 = n0.z;
    squad.b1 = n2.z;
    squad.b0sq = squad.b0 * squad.b0;
    squad.k = 2.*PI - g2 - g3;
    // compute solid angle from internal angles
    squad.S = g0 + g1 - squad.k;
}

void SphQuadSample(in vec3 x, SphQuad squad, float u, float v, out LightSamplingRecord sampleRec) {
    // 1. compute cu
    float au = u * squad.S + squad.k;
    float fu = (cos(au) * squad.b0 - squad.b1) / sin(au);
    float cu = 1./sqrt(fu*fu + squad.b0sq) * (fu>0. ? +1. : -1.);
    cu = clamp(cu, -1., 1.); // avoid NaNs
    // 2. compute xu
    float xu = -(cu * squad.z0) / sqrt(1. - cu*cu);
    xu = clamp(xu, squad.x0, squad.x1); // avoid Infs
    // 3. compute yv
    float d = sqrt(xu*xu + squad.z0sq);
    float h0 = squad.y0 / sqrt(d*d + squad.y0sq);
    float h1 = squad.y1 / sqrt(d*d + squad.y1sq);
    float hv = h0 + v * (h1-h0), hv2 = hv*hv;
    float yv = (hv2 < 1.-EPSILON) ? (hv*d)/sqrt(1.-hv2) : squad.y1;
    // 4. transform (xu,yv,z0) to world coords

    vec3 p = (squad.o + xu*squad.x + yv*squad.y + squad.z0*squad.z);
    sampleRec.w = p - x;
    sampleRec.d = length(sampleRec.w);
    sampleRec.w = normalize(sampleRec.w);
    sampleRec.pdf = 1. / squad.S;
}

vec3 sampleLightSource(in vec3 x, in vec3 n, float Xi1, float Xi2, out LightSamplingRecord sampleRec) {
    vec3 s = light.pos - vec3(1., 0., 0.) * light.size.x * 0.5 -
        				 vec3(0., 0., 1.) * light.size.y * 0.5;
    vec3 ex = vec3(light.size.x, 0., 0.);
    vec3 ey = vec3(0., 0., light.size.y);

    SphQuad squad;
    SphQuadInit(s, ex, ey, x, squad);
    SphQuadSample(x, squad, Xi1, Xi2, sampleRec);

    //we don't have normal for volumetric particles
    if(dot(n,n) < EPSILON) {
        SphQuadSample(x, squad, Xi1,Xi2, sampleRec);
    } else {
        LightSamplingRecord w[CDF_SIZE];
        float ww[CDF_SIZE];
        const float strata = 1.0 / float(CDF_SIZE);
        for(int i=0; i<CDF_SIZE; i++) {
            float xi = strata*(float(i)+rnd());
            SphQuadSample(x, squad, xi, rnd(), w[i]);
            ww[i] = (i == 0)? 0.0 : ww[i-1];
            ww[i] += max(0.0, dot(w[i].w, n));
        }

        float a = Xi1 * ww[CDF_SIZE-1];
        for(int i=0; i<CDF_SIZE; i++) {
            if(ww[i] > a) {
                sampleRec = w[i];
                sampleRec.pdf *= (ww[i] - ((i == 0)? 0.0 : ww[i-1])) / ww[CDF_SIZE-1];
                sampleRec.pdf *= float(CDF_SIZE);
                break;
            }
        }
    }

	return getRadiance(vec2(Xi1,Xi2));
}

float sampleLightSourcePdf(in vec3 x, vec3 ns, in vec3 wi, float d, float cosAtLight) {
    vec3 s = light.pos - vec3(1., 0., 0.) * light.size.x * 0.5 -
        				 vec3(0., 0., 1.) * light.size.y * 0.5;
    vec3 ex = vec3(light.size.x, 0., 0.);
    vec3 ey = vec3(0., 0., light.size.y);

    SphQuad squad;
    SphQuadInit(s, ex, ey, x, squad);
    return 1. / squad.S;
}
