bool isLightVisible( Ray shadowRay ) {
    float distToHit;
    SurfaceHitInfo tmpHit;

    raySceneIntersection( shadowRay, EPSILON, true, tmpHit, distToHit );

    return ( tmpHit.mtl_id_ >= LIGHT_ID_BASE );
}

void fixMtl(inout Material mtl, vec2 uv, out vec3 ns) {
    uv *= mtl.tex_scale_;
    if(mtl.diffuse_color_tex_!=-1){
        mtl.diffuse_color_ = getColor(uv, mtl.diffuse_color_tex_);
    }

    if(mtl.specular_roughness_tex_!=-1){
        mtl.specular_roughness_ = (1.0 - sqrt(getColor(uv, mtl.specular_roughness_tex_).x));
    } else {
        mtl.specular_roughness_ = sqrt(mtl.specular_roughness_);
    }

    if(mtl.specular_weight_tex_!=-1){
        mtl.specular_weight_ = (getColor(uv, mtl.specular_weight_tex_).x);
    }
    if(mtl.normal_map_!=-1){
        ns = getNormal(uv, mtl.normal_map_ );
    } else {
        ns = vec3(0.0, 0.0, 1.0);
    }
}

vec3 sampleBSDF(	in vec3 x,
                  	in vec3 ng,
                  	in vec3 ns,
                	in vec3 wi,
                	in float time,
                  	in Material mtl,
                	out vec3 wo,
                	out float brdfPdfW,
                	out vec3 fr,
                	out bool hitRes,
                	out SurfaceHitInfo hit) {
    vec3 Lo = vec3(0.0);
    float Xi1 = rnd();
    float Xi2 = rnd();
    fr = mtlSample(mtl, ng, ns, wi, Xi1, Xi2, wo, brdfPdfW);

    //fr = eval(mtl, ng, ns, wi, wo);

    float dotNWo = dot(wo, ns);
    //Continue if sampled direction is under surface
    if ((dot(fr,fr)>0.0) && (brdfPdfW > EPSILON)) {
        Ray shadowRay = Ray(x + ng*EPSILON, wo, time);

        //abstractLight* pLight = 0;
        float cosAtLight = 1.0;
        float distanceToLight = -1.0;
        vec3 Li = vec3(0.0);

        float distToHit;

        if(raySceneIntersection( shadowRay, EPSILON, false, hit, distToHit )) {
            if(hit.mtl_id_>=LIGHT_ID_BASE) {
                distanceToLight = distToHit;
                cosAtLight = dot(hit.normal_, -wo);
                if(cosAtLight > 0.0) {
                    Li = getRadiance(hit.uv_);
                    //Li = lights[0].color_*lights[0].intensity_;
                }
            } else {
                hitRes = true;
            }
        } else {
            hitRes = false;
            //TODO check for infinite lights
        }

        if (distanceToLight>0.0) {
            if (cosAtLight > 0.0) {
                Lo += ((Li * fr * dotNWo) / brdfPdfW) * misWeight(brdfPdfW, sampleLightSourcePdf(x, ns, wi, distanceToLight, cosAtLight));
            }
        }
    }

    return Lo;
}

vec3 salmpleLight(	in vec3 x,
                  	in vec3 ng,
                  	in vec3 ns,
                  	in vec3 wi,
                  in float time,
                  	in Material mtl) {
    vec3 Lo = vec3(0.0);	//outgoing radiance
	const float strata = 1.0 / float(SURFACE_DIRECT_LIGHT_SAMPLES);
    for(int i=0; i<SURFACE_DIRECT_LIGHT_SAMPLES; i++){
        LightSamplingRecord rec;
        float xi = strata*(float(i)+rnd());
        vec3 Li = sampleLightSource( x, ns, xi, rnd(), rec );

        float dotNWo = dot(rec.w, ns);
        if ((dotNWo > 0.0) && (rec.pdf > EPSILON)) {
            vec3 fr = mtlEval(mtl, ng, ns, wi, rec.w);
            if(dot(fr,fr)>0.0) {
                Ray shadowRay = Ray(x + ng*EPSILON, rec.w, time);
                if (isLightVisible( shadowRay )) {
                    Lo += ((Li * fr * dotNWo) / rec.pdf) * misWeight(rec.pdf, mtlPdf(mtl, ng, ns, wi, rec.w));
                }
            }
        }
    }

    return Lo / float(SURFACE_DIRECT_LIGHT_SAMPLES);
}

vec3 salmpleLightForParticle(in vec3 x, in float time) {
    vec3 Lo = vec3(0.0);	//outgoing radiance

    LightSamplingRecord rec;
    vec3 Li = sampleLightSource( x, vec3(0.0), rnd(), rnd(), rec );

    float dotNWo = rec.w.y;
    if (dotNWo > 0.02 && rec.pdf > EPSILON) {
        float fr = VOLUME_SCAATTERING / (4.0 * PI);
        float phasePdf = 1.0 / (2.0 * PI);

        bool v = true;
#ifdef VOLUMETRIC_SHADOWS
        Ray shadowRay = Ray(x, rec.w, time);
        v = isLightVisible( shadowRay );
#endif
        Lo += ((Li * fr * float(v)) / rec.pdf) * misWeight(rec.pdf, phasePdf);
    }

    return Lo;
}


//tacken from sjb
void sampleEquiAngular(
	Ray ray,
	float maxDistance,
	float Xi,
	vec3 lightPos,
	out float dist,
	out float pdf)
{
	// get coord of closest point to light along (infinite) ray
	float delta = dot(lightPos - ray.origin, ray.dir);

	// get distance this point is from light
	float D = length(ray.origin + delta*ray.dir - lightPos);

	// get angle of endpoints
	float thetaA = atan(0.0 - delta, D);
	float thetaB = atan(maxDistance - delta, D);

	// take sample
	float t = D*tan(mix(thetaA, thetaB, Xi));
	dist = delta + t;
	pdf = D/((thetaB - thetaA)*(D*D + t*t));
}
