vec3 DirectLightOnVolume(in Ray ray, in float len) {
    const float strata = 1.0/float(VOLUME_DIRECT_LIGHT_SAMPLES);
    vec3 volumeDirectLight = vec3(0.);
    for(int i=0; i<VOLUME_DIRECT_LIGHT_SAMPLES; i++) {
        float particleDist;
        float particlePdf;
        //avoid sampling other side of the light
        float maxt = -(ray.origin.y - light.pos.y) / ray.dir.y;//doesn't work :(
        len = (ray.dir.y > 0.0) ? min(maxt, len) : len;
        float xi = strata*(float(i)+rnd());

        sampleEquiAngular( ray, len, xi, light.pos, particleDist, particlePdf );
        vec3 particlePos = ray.origin + particleDist*ray.dir;
        volumeDirectLight += salmpleLightForParticle(particlePos, ray.time) / particlePdf;
        //volumeDirectLight += samplePhaseForParticle(particlePos, ray.time) / particlePdf;
    }
    return volumeDirectLight / float(VOLUME_DIRECT_LIGHT_SAMPLES);
}

vec3 Radiance(in Ray r) {
    vec3 Lo = vec3(0.0), fr, directLight, pathWeight = vec3(1.0, 1.0, 1.0);
    vec3 wo;
    float woPdf;
    float dotWoN;
    bool hitResult;

    //Calculate first intersections to determine first scattering event
    Ray ray = r;
    SurfaceHitInfo event;
    SurfaceHitInfo nextEvent;
    float dist = 1000.0;
    if(!raySceneIntersection( ray, 0.0, false, event, dist )) {
        //return Lo;
        dist = 1000.0;
    } else {
        //We have to add emmision component on first hit
        if( event.mtl_id_ >= LIGHT_ID_BASE ) {
            //Light light = getLight(event.mtl_id_ - LIGHT_ID_BASE);
            float cosAtLight = dot(event.normal_, -ray.dir);
            if(cosAtLight > 0.0) {
                Lo = getRadiance(event.uv_);
            }
        }
    }

    float prev_spec;

    for (int i = 0; i < MAX_DEPTH; i++) {
        if(VOLUME_SCAATTERING!=0.0) {
        	Lo += DirectLightOnVolume(ray, dist)*pathWeight;
        }


        if(dist > 999.0 || event.mtl_id_>=LIGHT_ID_BASE){
        	break;
    	}
        vec3 x = event.position_;
        vec3 wi = -ray.dir;
        event.normal_ = -faceforward(event.normal_, wi, event.normal_);

        Material mtl = getMaterial(event.mtl_id_);
    	vec3 ng = event.normal_, ns;

    	fixMtl(mtl, event.uv_, ns);

        mat3 frame;
        frame[0] = event.tangent_;
        frame[1] = cross( ng, event.tangent_ );
        frame[2] = ng;
        ns = frame*ns;

        if (dot(wi,ns) < 0.0) { break; }

        //Calculate direct light with 'Light sampling' and 'BSDF sampling' techniques
        //In addition BSDF sampling does next event estimation and returns all necessary values which corresponds to next event
       	directLight  = salmpleLight (x, ng, ns, wi, ray.time, mtl);
        directLight += sampleBSDF   (x, ng, ns, wi, ray.time, mtl, wo, woPdf, fr, hitResult, nextEvent);


        Lo += directLight*pathWeight;

        if (!hitResult || (dotWoN = dot(event.normal_, wo))<0.0) { break; }
        if (woPdf == 0.0) { break; }
        pathWeight *= fr*dotWoN / woPdf;

        //Update values for next iteration
        dist = length(nextEvent.position_ - event.position_);
        ray = Ray(event.position_, wo, r.time);
        event = nextEvent;
    }

    return vec3(Lo);
}

vec3 ClampColor(in vec3 c, in float maxVal) { return min(c, vec3(maxVal)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    seed = iTime + iResolution.y * fragCoord.x / iResolution.x + fragCoord.y / iResolution.y;

    vec2 renderingResolution = min(iResolution.xy, vec2(float(RENDERING_RES_X), float(RENDERING_RES_Y)));

    vec2 pixelCoord = fragCoord.xy;
    if(fragCoord.x > renderingResolution.x ||
       fragCoord.y > renderingResolution.y) {
        //fragColor = vec4( 0.0 );
        discard;
    } else {
        //float fov = radians(30.0);//mix( radians(17.0), radians(7.0), pow(clamp(float(iFrame - 120) / 100.0, 0.0, 1.0),2.0));
        Camera camera = readCamera(iChannel0);

        initScene();

        float dist_min = 1000.0;
        float ratio = renderingResolution.x/renderingResolution.y;
        float var = 0.0;
        vec3 accumulatedColor = vec3( 0.0 );
        float tStrata = 1.0/float(PIXEL_SAMPLES);
        for( int si=0; si<PIXEL_SAMPLES; ++si ){
            vec2 jitter = vec2(rnd(), rnd());
            vec2 screen_uv = (pixelCoord.xy + jitter) / renderingResolution;//iResolution.xy;
            Ray ray = genRay( camera, screen_uv, ratio, /*tStrata*(float(si)+rnd())*/rnd(), rnd(), tStrata*(float(si)+rnd()) );
            vec3 rad = Radiance(ray);
            rad = ClampColor(rad, CLAMP_VALUE);
            float la = length(accumulatedColor);
            if(si > 0) { var += abs(length(rad) - la) / (la == 0.0? 1.0 : la);}
            accumulatedColor += rad;
        }

        //devide to sample count
        accumulatedColor /= float(PIXEL_SAMPLES);
        var /= float(PIXEL_SAMPLES);

        fragColor = vec4( accumulatedColor, var );
    }
}
