#define PI 3.14159265359

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    float d = length(uv-3.75);
    float f = sin(d);
	vec2 u=fract(uv*f);
    float t = iTime*.51;
    t+=sin(t*2.0);
    u-=0.5;
    vec2 m = uv-vec2(0.5,0.2);
    float a = atan(m.y,m.x);
    u = rotate2d( a+t*PI ) * u;
    u+=0.5;
//    float k = smoothstep(0.5,0.51,u.x);
    float k = smoothstep(.5,.51,u.x);

    //  float k = smoothstep(uv.x-0.5,uv.y+0.5,u.x);
vec3 final = mix(vec3(1.5,0.2,0.1),vec3(0.0,0.0,.2),k);
    fragColor = vec4(k);
}
