mat3 getRotXMat(float a){return mat3(1.,0.,0.,0.,cos(a),-sin(a),0.,sin(a),cos(a));}
mat3 getRotYMat(float a){return mat3(cos(a),0.,sin(a),0.,1.,0.,-sin(a),0.,cos(a));}
mat3 getRotZMat(float a){return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}

mat3 mat;

vec2 path(float t)
{
	return vec2(cos(t*0.08), sin(t*0.08)) * 4.;
}

float pattern(vec3 p)
{
	p = abs(fract(p) - 0.5);
	p = max(abs(max(max(p.xyz, -p.xyz) + p.yxz, -p.yxz)) + p.zxy, -p.zxy);
	return length(p) - 0.;
}

vec3 effect(vec3 p) 
{
	float d = min(pattern(p*mat), pattern(p*mat*mat)) / .9; // based on a sahder form shane

    return vec3(d); 
}

vec4 displacement(vec3 p)
{
	vec3 col = 1.-clamp(effect(p*0.24),0.,1.);
   	float dist = dot(col,vec3(1));
	col = step(col, vec3(0));
    return vec4(dist,col);
}

vec4 map(vec3 p)
{
	p.xy -= path(p.z);
    vec4 disp = displacement(p);
    return vec4(5. - length(p.xy) - disp.x*.5 + sin(p.x*p.y*.5-p.z*3.)*0.1, disp.yzw);
}

