float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}


///-----------------------------------------------------
vec3 opAngRep( vec3 p, float a )
{
	vec2 polar = vec2(atan(p.y, p.x), length(p.xy));
    polar.x = mod(polar.x + a / 2.0, a) - a / 2.0;

    return vec3(polar.y * vec2(cos(polar.x),sin(polar.x)), p.z);
}

vec3 hacerCoronaNueva(vec3 pp  )
{
    vec3 p=pp;
    float numItem=8.0;
    float tau = atan(1.0) * 8.0;
    vec3 res= vec3(9999.0, -1.0,-1.0);

    p= rotate_x( p, radians(90.0));


    float st1= sdTorus( p, vec2(5.0,0.5) );
    float st2= sdTorus( p, vec2(10.0,0.5) );

    p=pp;
    vec3 rep2 = opAngRep(p, tau / numItem);
    rep2= rotate_z( rep2-vec3(10.0,0.0,0.0), radians(90.0));
    rep2= rotate_x( rep2, radians(180.0));

    float scc1= sdCappedCone( rep2, 5.0, 0.025, 0.5 );

    res =opU3(res, vec3(st1,4.0,-1.0));
    res =opU3(res, vec3(st2,32.0,-1.0));
    res =opU3(res, vec3(scc1,32.0,-1.0));

    return res;

}

