vec3 opAngRep( vec3 p, float a )
{
	vec2 polar = vec2(atan(p.y, p.x), length(p.xy));
    polar.x = mod(polar.x + a / 2.0, a) - a / 2.0;

    return vec3(polar.y * vec2(cos(polar.x),sin(polar.x)), p.z);
}

vec3 cohete(vec3 p)
{ 
   vec3 res= vec3(9999.0, -1.0,-1.0); 
   vec3 p0=p;
   p=rotate_y(p-vec3(0.0,5.0,10.0), radians(270.0));
   vec3 p1=p;

   p=rotate_y(p-vec3(0.0,5.0,10.0), radians(90.0));

   float sd1A=sdCylinderYZ(p, vec2(5.0,10.0));
   float sd1B=sdCylinderYZ(p, vec2(4.0,10.5));
   float sd1C=sdCylinderYZ(p, vec2(4.0,9.0));

   vec3 p2=p0-vec3(26.0,10.0,8.0);
   vec3 p3=p0-vec3(2.0,10.0,8.0);

   p2=rotate_x(p2, radians(90.0));
   p2=rotate_z(p2, radians(90.0));

   p3=rotate_x(p3, radians(90.0));
   p3=rotate_z(p3, radians(90.0));

   float sdPunta1= sdCone(p2-vec3(2.0,0.0,0.0), vec2(3.5,5.5), 6.0);
   float sdPunta2= sdCone(p3-vec3(2.0,0.0,0.0), vec2(3.5,5.5), 6.0);
   float sdif= differenceSDF(sd1A, sd1B);

   p.y-=10.0;

   float numItem=6.0;
   float tau = atan(1.0) * 8.0;

   //rotar aspas
   p1=rotate_z(p1-vec3(0.0,5.0,-1.0), iTime);
   vec3 rep2a = opAngRep(p1, tau / numItem);
   float sdAspas=sdCylinderYZ(rep2a ,vec2(1.0,10.0)  );

   res =opU3(res, vec3(sdif,3.0,-1.0)); 
   res =opU3(res, vec3(sd1C,1.0,-1.0)); 
   res =opU3(res, vec3(sdAspas,2.0,-1.0)); 
   res =opU3(res, vec3(sdPunta1,4.0,-1.0)); 
   res =opU3(res, vec3(sdPunta2,5.0,-1.0)); 

   return res;
}

