// Fork of "vertical ribbon - Loie Fuller" by morisil. https://shadertoy.com/view/NsVGWh
// 2021-09-14 23:31:40

const int   RIBBON_COUNT = 13;
const float RIBBON_WIDTH = .005;
const float RIBBON_EDGE_WIDTH = .003;
const float RIBBON_EDGE_START = RIBBON_WIDTH - RIBBON_EDGE_WIDTH;
const float SCALE_CHANGE = .9;
const float SCALE_CHANGE_VARIATION = .02;
const float SCALE_CHANGE_SPEED = 1.7;
const float WAVE1_IMPACT = .1;
const float WAVE2_IMPACT = .3;


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float wave1Period = sin(iTime * .3) * 30.;
    float wave2Period = sin(iTime * .4) * 10.;
    float wave1Speed = sin(iTime * .5) * .02;
    float wave2Speed = sin(iTime * .7) * .01;    
    vec2 uv = fragCoord / iResolution.xy;
    vec2 st =
        (2.* fragCoord - iResolution.xy)
        / min(iResolution.x, iResolution.y);
    vec3 color = vec3(0);
    for (int i = 0; i < RIBBON_COUNT; i++) {
        st *= (
            SCALE_CHANGE
            + (
                sin(iTime * SCALE_CHANGE_SPEED)
                * SCALE_CHANGE_VARIATION
            )
        );
        float dist = length(st);
        float shapeSpace = abs(
            st.x
            + sin(st.y * wave1Period + iTime * wave1Speed) * WAVE1_IMPACT * (1.2 - uv.y)
            + sin(st.y * wave2Period + iTime * wave2Speed) * WAVE2_IMPACT * (1.1 - uv.y)
        );
        float ribbon = smoothstep(
            RIBBON_WIDTH,
            RIBBON_EDGE_START,
            shapeSpace
        );
        color += vec3(ribbon);
    }

    fragColor = vec4(color, 1);
}
