//The floor shows the internal distance/gradient
//Norm is scaled into 0-1 range for visualization to verify signs

//Set to 1 to show numeric gradient to verify that it matches analytic
#define SHOW_NUMERIC_GRADIENT 0

//if 1 it scales norm into 0-1, if 0 it takes abs
#define SHOW_FULL_NORM_RANGE 1


float sdgCylinder(vec3 p, vec2 rh,  out vec3 norm) {
	float lp = length(p.xy);
	vec2 a = vec2(lp - rh.x, abs(p.z) - rh.y);

	float bmax = max(a.x, a.y);;
	float d = bmax;

	bool useCircle = a.x >= a.y;
	norm = vec3(useCircle? p.xy: vec2(0.0,0.0), useCircle? 0.0:p.z);

	bool need2ndSqrt = min(a.x, a.y) >0.0;

	if (need2ndSqrt){//Only need this if at diagonal 
		vec2 b = max(a,0.0);
		d = sqrt( dot(b,b))+ min(bmax,0.0);
		norm = vec3(p.x, p.y, sign(p.z)* b.y) * vec3(b.x, b.x, lp);
	}
    //This version normalizes as it is likely less instructions than getting the gradient length correct analytially
    //For my use (triplanar texturing mostly) gradient length is irrelevant, so I just skip this
    norm = normalize(norm);
    return d;
}

//Version with purely analytic gradient length(TODO)
float sdgCylinderAnalyticGL(vec3 p, vec2 rh,  out vec3 norm) {
        float d2_xy = dot(p.xy,p.xy);
        float rsqrtD2 = inversesqrt(d2_xy);
		float lp = d2_xy*rsqrtD2;
		vec2 a = vec2(lp - rh.x, abs(p.z) - rh.y);

		float bmax = max(a.x, a.y);;
		float d = bmax;

		bool useCircle = a.x >= a.y;
        //correct
		norm = vec3(useCircle? p.xy*rsqrtD2: vec2(0.0,0.0), useCircle? 0.0:sign(p.z));

		bool need2ndSqrt = min(a.x, a.y) >0.0;

		if (need2ndSqrt){//Only need this if at diagonal 
			vec2 b = max(a,0.0);
			d = sqrt( dot(b,b))+ min(bmax,0.0);
            //TODO: correct the length
			norm = vec3(p.x, p.y, sign(p.z)* b.y) * vec3(b.x, b.x, lp);
            norm = normalize(norm);//cheat for now if we get inside this part
		}

		return d;
}


//Rest of code by mostly by paniq
void doCamera( out vec3 camPos, out vec3 camTar, in float time, in float mouseX )
{
    float an = 1.5 + sin(time * 0.1) * 0.7;
	camPos = vec3(4.5*sin(an),2.0,4.5*cos(an));
    camTar = vec3(0.0,0.0,0.0);
}

vec3 doBackground( void )
{
    return vec3( 0.0, 0.0, 0.0);
}

vec2 min2(vec2 a, vec2 b) {
    return (a.x <= b.x)?a:b;
}

vec2 max2(vec2 a, vec2 b) {
    return (a.x > b.x)?a:b;
}


vec2 plane( vec3 p) {
    return vec2(p.y+2.0,1.0);
}

vec2 add_plane(vec3 p, vec2 m) {
    return min2(plane(p),m);
}

vec2 doModelWithNorm( vec3 p, out vec3 norm) {

    float Size = 1.5;
   // float d = Tetrahedron(p, Size, 0.0);
   float round = .8*abs(sin(iTime*.3));
   round=round*round*round;
   float rd = abs(cos(iTime*.1))*.5 + 1.0;//,norm;
   vec2 rad = vec2(rd*.5, rd);
    float d = sdgCylinder(p, rad, norm )-round;

#if SHOW_NUMERIC_GRADIENT == 1
//  if(iMouse.z<=0.0){//show  numeric gradients when left mouse is held

     const float eps = 0.0001;
     const vec3 e = vec3(eps,0.0, 0.0);
     norm = normalize(vec3(sdgCylinder( p +e.xyz, rad, norm )- sdgCylinder( p -e.xyz, rad, norm ),
                      sdgCylinder( p +e.yxz, rad, norm )- sdgCylinder( p -e.yxz, rad, norm ),
                      sdgCylinder( p +e.yzx, rad, norm )- sdgCylinder( p -e.yzx, rad, norm )));
  //   }
#endif
#if SHOW_FULL_NORM_RANGE == 1
    norm = norm*.5 + .5;
 #else
    norm = abs(norm);
#endif

	// d = min(d,length(p)-Size);//use to verify sphere size and tet match up
    vec2 rr = add_plane(p, vec2(d,0.0));

    return rr;
}
vec2 doModel( vec3 p) {
vec3 norm;
    return doModelWithNorm(p, norm);
}
//------------------------------------------------------------------------
// Material 
//
// Defines the material (colors, shading, pattern, texturing) of the model
// at every point based on its position and normal. In this case, it simply
// returns a constant yellow color.
//------------------------------------------------------------------------
vec4 doMaterial( in vec3 pos, in vec3 nor )
{
    vec3 norm,norm2;
    float k = doModelWithNorm(pos,norm).y;
    float d = doModelWithNorm(vec3(pos.x,0.0,pos.z),norm2).x;

    float w = abs(mod(d, 0.1)/0.1 - 0.5);

    vec4 objCOlor = vec4(0.1,.01,1.0,0.1);
    objCOlor.xyz = norm;
    return mix(objCOlor, //nor * 0.5 + 0.5,
               vec4(norm2,0.0) * w,
               clamp(k,0.0,1.0));
}

//------------------------------------------------------------------------
// Lighting
//------------------------------------------------------------------------
float calcSoftshadow( in vec3 ro, in vec3 rd );

vec3 doLighting( in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in vec4 mal )
{
    vec3 lin = vec3(0.0);

    vec3  lig = normalize(vec3(1.0,0.7,0.9));
	float cos_Ol = max(0.0, dot(nor, lig));
    vec3 h = normalize(lig - rd);
    float cos_Oh = max(0.0,dot(nor, h));
    float dif = cos_Ol;
    float sha = 0.0; if( dif>0.01 ) sha=calcSoftshadow( pos+0.01*nor, lig );
    lin += dif*vec3(0.8, 0.7, 0.6)*sha;

    lin += vec3(0.20,0.30,0.30);


    vec3 col = mal.rgb*lin;

    // specular
    col += cos_Ol * pow(cos_Oh,40.0);

    // envmap
 //   col += mal.w*Texture2D(iChannel0, reflect(rd,nor)).rgb;

    // fog    
    //-----------------------------
	col *= exp(-0.01*dis*dis);

    return col;
}

float calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 20.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
	float res = -1.0;
    for( int i=0; i<80; i++ )          // max number of raymarching iterations is 90
    {
        if( h<precis||t>maxd ) break;

	    h = doModel( ro+rd*t ).x;
        t += h;
    }

    if( t<maxd ) res = t;
    return res;
}

vec3 calcNormal( in vec3 pos )
{

#if 0
    vec3 norm;
    doModelWithNorm(pos, norm);
#if SHOW_FULL_NORM_RANGE ==1
    norm = norm*2.0 -1.0;
#endif
    return (norm);

#else//for lighting we want the ground plane etc, so do it numerically
        const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ).x + 
					  v2*doModel( pos + v2*eps ).x + 
					  v3*doModel( pos + v3*eps ).x + 
					  v4*doModel( pos + v4*eps ).x );
    vec3 norm;
#endif
}

float calcSoftshadow( in vec3 ro, in vec3 rd )
{
    float res = 1.0;
    float t = 0.0005;                 // selfintersection avoidance distance
	float h = 1.0;
    for( int i=0; i<80; i++ )         // 40 is the max numnber of raymarching steps
    {
        h = doModel(ro + rd*t).x;
        res = min( res, 64.0*h/t );   // 64 is the hardness of the shadows
        if(h <= 0.0){
            break;
        }
        t +=abs(h);
        if(t > 20.0){
            break;
        }
      //  t += max(h, 0.;
	//	t += clamp( h, 0.001, 2.0 );   // limit the max and min stepping distances
    }
    return clamp(res,0.0,1.0);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

vec3 ff_filmic_gamma3(vec3 linear) {
    vec3 x = max(vec3(0.0), linear-0.004);
    return (x*(x*6.2+0.5))/(x*(x*6.2+1.7)+0.06);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

  //  vec2 m = vec2(0.5);
	if( iMouse.z>0.0 ) m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
   // vec3 ro, ta;
    //doCamera( ro, ta, iTime, m.x );
    //doCamera( ro, ta, 3.0, 0.0 );

    // camera matrix
   // mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	//vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length
    	float an = 1.1 + 0.05*(iTime-10.0) - 7.0*m.x+1.0;

	vec3 ro = vec3(4.5*sin(an),1.0,4.5*cos(an));
    vec3 ta = vec3(0.0,0.2,0.0);
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	// create view ray
	vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );


    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = doBackground();

	// raymarch
    float t = calcIntersection( ro, rd );
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);

        // materials
        vec4 mal = doMaterial( pos, nor );

        col = doLighting( pos, nor, rd, t, mal );
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = ff_filmic_gamma3(col * 0.6); //pow( clamp(col,0.0,1.0), vec3(0.4545) );

    fragColor = vec4( col, 1.0 );
}
